import React from 'react';

const AboutUs = () => {
  return (
    <div style={styles.container}>
      <h1 style={styles.heading}>About Us</h1>
      <p style={styles.text}>
        Established in 2015, Flair Concepts & Media is dedicated to helping businesses, artists,
        and organizations amplify their voices through professional media services. Our CIPC
        Registration Number is 2015/333836/07.
      </p>
    </div>
  );
};

const styles = {
  container: {
    maxWidth: '800px',
    margin: '0 auto',
    padding: '20px',
    fontFamily: 'Arial, sans-serif',
  },
  heading: {
    fontSize: '24px',
    fontWeight: 'bold',
    marginBottom: '20px',
  },
  text: {
    fontSize: '16px',
    lineHeight: '1.6',
  },
};

export default AboutUs;
