import React from 'react';

const QualityAssurance = () => {
  return (
    <div style={styles.container}>
      <div style={styles.textContainer}>
        <h1 style={styles.heading}>
          <span style={styles.quality}>QUALITY</span>
          <span style={styles.assurance}>ASSURANCE</span>
        </h1>
        <p style={styles.paragraph}>
          We are committed to meeting the needs of our customers, staff, and stakeholders to ensure that services are carried out to client satisfaction and maintain the integrity and sustainability of the environment.
        </p>
        <a href="/read-more" style={styles.readMoreLink}>Read More</a>
      </div>
      <div style={styles.imageContainer}>
        <img
          src={require("./../../assets/heropage.png")}
          alt="Logo"
          style={{
            height: "500px",
            width: "500px",  // Increased size
            borderRadius: "20px"  // Rounded corners
          }}
        />
      </div>
    </div>
  );
};

const styles = {
  container: {
    display: 'flex',
    alignItems: 'center',
    justifyContent: 'center', // Center the content horizontally
    padding: '40px 20px',
    backgroundColor: '#ffffff',
    border: '1px solid #e0e0e0',
    borderRadius: '10px',
    maxWidth: '1200px',
    margin: '0 auto',
    fontFamily: 'Arial, sans-serif',
    boxShadow: '0 4px 8px rgba(0, 0, 0, 0.1)',
    flexDirection: 'row', // Side by side on larger screens
    flexWrap: 'wrap', // Allow content to wrap on smaller screens
  },
  textContainer: {
    flex: 1,
    paddingRight: '40px',
    textAlign: 'center',  // Center the text inside the text container
    marginBottom: '20px', // Space at the bottom for mobile screens
  },
  heading: {
    fontSize: '36px',
    fontWeight: 'bold',
    textTransform: 'uppercase',
    marginBottom: '20px',
  },
  quality: {
    color: '#28a745', // Green color for "QUALITY"
    display: 'block', // Ensure "QUALITY" is on top
  },
  assurance: {
    color: '#fd7e14', // Orange color for "ASSURANCE"
    display: 'block', // Ensure "ASSURANCE" is below
  },
  paragraph: {
    fontSize: '18px',
    color: '#555555',
    lineHeight: '1.8',
    marginBottom: '30px',
  },
  readMoreLink: {
    display: 'inline-block',
    padding: '12px 24px',
    backgroundColor: '#fd7e14', // Orange background for the button
    color: '#ffffff',
    textDecoration: 'none',
    borderRadius: '5px',
    fontSize: '16px',
    fontWeight: '600',
    transition: 'background-color 0.3s ease',
  },
  imageContainer: {
    flex: 1,
    textAlign: 'center', // Center the image container
    marginBottom: '20px', // Add some space at the bottom for mobile screens
  },
  image: {
    maxWidth: '100%',
    height: 'auto',
    borderRadius: '10px',
  },
  // Mobile responsive styles
  '@media (max-width: 768px)': {
    container: {
      flexDirection: 'column',  // Stack text and image vertically on mobile
      padding: '20px',
    },
    textContainer: {
      paddingRight: '0', // Remove right padding for smaller screens
    },
    heading: {
      fontSize: '28px', // Reduce heading size for mobile
    },
    paragraph: {
      fontSize: '16px', // Reduce paragraph size for mobile
    },
    image: {
      height: '300px',  // Reduce image size on mobile
      width: '300px',   // Reduce width on mobile
    },
  }
};

export default QualityAssurance;
