import React, { useState } from 'react';
// import './Header.css';

const Header = () => {
  const [isOpen, setIsOpen] = useState(false);
  const [sidebarOpen, setSidebarOpen] = useState(false);

  const handleToggle = () => {
    setIsOpen(!isOpen);
  };

  const handleSidebarToggle = () => {
    setSidebarOpen(!sidebarOpen);
  };

  return (
    <header style={{
      backgroundColor: '#333',
      color: '#fff',
      padding: '1em',
      display: 'flex',
      justifyContent: 'space-between',
      alignItems: 'center'
    }}>
      <div style={{
        fontSize: '1.5em',
        fontWeight: 'bold'
      }}>
        Logo
      </div>
      <nav>
        <ul style={{
          listStyle: 'none',
          margin: 0,
          padding: 0,
          display: 'flex'
        }}>
          <li style={{
            marginRight: '20px'
          }}>
            <button onClick={handleToggle} style={{
              backgroundColor: '#333',
              color: '#fff',
              border: 'none',
              padding: '10px 20px',
              fontSize: '16px',
              cursor: 'pointer'
            }}>
              Options
            </button>
            {isOpen && (
              <ul style={{
                position: 'absolute',
                backgroundColor: '#333',
                padding: '10px',
                border: '1px solid #444',
                display: 'block'
              }}>
                <li style={{
                  marginBottom: '10px'
                }}>
                  <a href="#" style={{
                    color: '#fff',
                    textDecoration: 'none'
                  }}>
                    Option 1
                  </a>
                </li>
                <li style={{
                  marginBottom: '10px'
                }}>
                  <a href="#" style={{
                    color: '#fff',
                    textDecoration: 'none'
                  }}>
                    Option 2
                  </a>
                </li>
                <li style={{
                  marginBottom: '10px'
                }}>
                  <a href="#" style={{
                    color: '#fff',
                    textDecoration: 'none'
                  }}>
                    Option 3
                  </a>
                </li>
              </ul>
            )}
          </li>
          <li>
            <button onClick={handleSidebarToggle} style={{
              backgroundColor: '#333',
              color: '#fff',
              border: 'none',
              padding: '10px 20px',
              fontSize: '16px',
              cursor: 'pointer'
            }}>
              Menu
            </button>
          </li>
        </ul>
      </nav>
      {sidebarOpen && (
        <div style={{
          position: 'fixed',
          top: 0,
          left: 0,
          width: '200px',
          height: '100vh',
          backgroundColor: '#333',
          padding: '20px',
          display: 'block'
        }}>
          <ul style={{
            listStyle: 'none',
            margin: 0,
            padding: 0
          }}>
            <li style={{
              marginBottom: '10px'
            }}>
              <a href="#" style={{
                color: '#fff',
                textDecoration: 'none'
              }}>
                Menu Item 1
              </a>
            </li>
            <li style={{
              marginBottom: '10px'
            }}>
              <a href="#" style={{
                color: '#fff',
                textDecoration: 'none'
              }}>
                Menu Item 2
              </a>
            </li>
            <li style={{
              marginBottom: '10px'
            }}>
              <a href="#" style={{
                color: '#fff',
                textDecoration: 'none'
              }}>
                Menu Item 3
              </a>
            </li>
          </ul>
        </div>
      )}
    </header>
  );
};

export default Header;
