import React from 'react';

const Services = () => {
  return (
    <div style={styles.container}>
      <img src={require("./../../assets/logo.png")} alt="Logo" style={styles.logo} />
      <h1 style={styles.title}>Public Relations Services</h1>
      <p style={styles.description}>
        We offer PR services for NPOs, community developers, corporate clients, artists, producers, DJs, and other creatives.
      </p>

      <div style={styles.section}>
        <h2 style={styles.sectionTitle}>PR Rates</h2>
        <div style={styles.grid}>
          <div style={styles.card}>
            <img src={require('./../../assets/1.png')} alt="NPO & Community" style={styles.image} />
            <h3 style={styles.cardTitle}>NPO & Community</h3>
          </div>
          <div style={styles.card}>
            <img src={require('./../../assets/2.png')} alt="Producers/DJs" style={styles.image} />
            <h3 style={styles.cardTitle}>Producers/DJs</h3>
          </div>
          <div style={styles.card}>
            <img src={require('./../../assets/3.png')} alt="Corporate Clients" style={styles.image} />
            <h3 style={styles.cardTitle}>Corporate Clients</h3>
          </div>
          <div style={styles.card}>
            <img src={require('./../../assets/4.png')} alt="Promotional Article" style={styles.image} />
            <h3 style={styles.cardTitle}>Promotional Article</h3>
          </div>
          <div style={{ ...styles.card, transform: 'scale(0.9)' }}>
      <img src={require('./../../assets/5.png')} alt="Additional Service" style={{ ...styles.image, width: '80%', height: 'auto' }} />
      <h3 style={styles.cardTitle}>Additional Service</h3>
    </div>
        </div>
      </div>
    </div>
  );
};

const styles = {
  container: {
    padding: '20px',
    fontFamily: 'Arial, sans-serif',
    textAlign: 'center', // Centering all text inside the container
  },
  logo: {
    width: '150px', // You can adjust the width of the logo here
    marginBottom: '20px', // Adds some space below the logo
  },
  title: {
    fontSize: '24px',
    fontWeight: 'bold',
    marginBottom: '10px',
  },
  description: {
    fontSize: '16px',
    marginBottom: '20px',
  },
  section: {
    marginBottom: '20px',
  },
  sectionTitle: {
    fontSize: '20px',
    fontWeight: 'bold',
    marginBottom: '15px',
    color: '#2461a4', // Set the color to blue
  },
  grid: {
    display: 'grid',
    gridTemplateColumns: 'repeat(auto-fit, minmax(200px, 1fr))',
    gap: '20px',
    justifyItems: 'center', // Center grid items
  },
  card: {
    textAlign: 'center',
    padding: '20px',
    borderRadius: '8px',
  },
  image: {
    width: '100%',
    height: 'auto',
    marginBottom: '10px',
  },
  cardTitle: {
    fontSize: '18px',
    fontWeight: 'bold',
  },
};

export default Services;
