import React from 'react';

const AboutUs = () => {
  return (
    <div style={styles.container}>
      <h1 style={styles.heading}>About Us</h1>
      <p style={styles.text}>
        Established in 2015, Flair Concepts & Media is dedicated to helping businesses, artists,
        and organizations amplify their voices through professional media services. Our CIPC
        Registration Number is 2015/333836/07.
      </p>
    </div>
  );
};

const styles = {
  container: {
    display: 'flex',
    flexDirection: 'column',
    justifyContent: 'center',
    alignItems: 'center',
    height: '100vh', // Full viewport height
    backgroundColor: 'white', // Light background color
    padding: '20px',
    fontFamily: 'Arial, sans-serif',
    textAlign: 'center', // Center-align text
  },
  heading: {
    fontSize: '24px',
    fontWeight: 'bold',
    marginBottom: '20px',
    color: '#333', // Dark text color
  },
  text: {
    fontSize: '16px',
    lineHeight: '1.6',
    color: '#555', // Slightly lighter text color
    maxWidth: '600px', // Limit text width for better readability
  },
};

export default AboutUs;
