import React from 'react';
import { mdiCellphone, mdiWeb } from '@mdi/js';
import Icon from '@mdi/react';

const Services = () => {
  return (
    <>
      <section className="services-section">
        <div className="services-content">
          <div className="services-text">
            <h1 style={{ color: 'white', fontSize: '3rem' }}>Our Services</h1>
            <p className="section-subtitle" style={{ color: '#5cb6f9', fontSize: '1.5rem' }}>Expertise in Mobile and Web Development</p>
          </div>
          <div className="services-cards">
            <div className="services-card">
              <Icon path={mdiCellphone} size={2} color="#5cb6f9" className="jumping-icon" />
              <h2 style={{ color: '#5cb6f9' }}>Mobile App Development</h2>
              <p style={{ color: 'white', fontSize: '1.5rem' }}>
                We design and develop mobile apps for Android and iOS devices. Our team of experts uses the latest technologies to create fast, secure, and user-friendly apps.
              </p>
              <ul style={{ color: 'white', fontSize: '1.5rem' }}>
                <li>Native App Development</li>
                <li>Cross-Platform App Development</li>
                <li>App Maintenance and Updates</li>
              </ul>
            </div>
            <div className="services-card">
              <Icon path={mdiWeb} size={2} color="#5cb6f9" className="jumping-icon" />
              <h2 style={{ color: '#5cb6f9' }}>Web Development</h2>
              <p style={{ color: 'white', fontSize: '1.5rem' }}>
                We build fast, secure, and scalable websites using the latest web technologies. Our team of experts has experience in developing custom websites, e-commerce platforms, and web applications.
              </p>
              <ul style={{ color: 'white', fontSize: '1.5rem' }}>
                <li>Custom Website Development</li>
                <li>E-commerce Website Development</li>
                <li>Web Application Development</li>
              </ul>
            </div>
          </div>
        </div>
      </section>

      <style jsx>{`
        .services-section {
          padding: 100px 0;
          background-color: #00183d; 
        }

        .services-content {
          max-width: 1200px;
          margin: 0 auto;
          padding: 0 20px;
        }

        .services-text {
          margin-bottom: 40px;
          text-align: center;
        }

        .services-cards {
          display: flex;
          justify-content: space-between;
          align-items: center;
          flex-wrap: wrap;
        }

        .services-card {
          background-color: #01224a;
          padding: 20px;
          border-radius: 10px;
          box-shadow: 0 0 10px rgba(0, 0, 0, 0.1);
          width: calc(45% - 20px);
          margin: 10px;
          display: flex;
          flex-direction: column;
          align-items: center;
        }

        @media (max-width: 768px) {
          .services-cards {
            flex-direction: column;
            align-items: center;
          }

          .services-card {
            width: 90%;
            margin: 10px 0;
          }
        }

        .services-card h2 {
          margin-top: 20px;
          color: #02f7ee;
        }

        .services-card ul {
          list-style: none;
          padding: 0;
          margin: 0;
        }

        .services-card li {
          margin-bottom: 10px;
          color: white;
        }

        /* Animation for jumping icons */
        .jumping-icon {
          animation: jump 1s ease-in-out infinite;
        }

        @keyframes jump {
          0% {
            transform: translateY(0);
          }
          50% {
            transform: translateY(-10px);
          }
          100% {
            transform: translateY(0);
          }
        }
      `}</style>
    </>
  );
};

export default Services;
