import React from 'react';

const QualityAssurance = () => {
  return (
    <div style={styles.container}>
      <div style={styles.content}>
        <h1 style={styles.heading}>
          <span style={styles.quality}>QUALITY</span>
          <span style={styles.assurance}>ASSURANCE</span>
        </h1>
        <p style={styles.paragraph}>
          We are committed to meeting the needs of our customers, staff and stakeholders to ensure that services are carried out to client satisfaction and maintain the integrity and sustainability of the environment.
        </p>
        <a href="/read-more" style={styles.readMoreLink}>Read More</a>
      </div>
      <div style={styles.imageContainer}>
        <img
          src="https://via.placeholder.com/400x300" // Replace with your image URL
          alt="Quality Assurance"
          style={styles.image}
        />
      </div>
    </div>
  );
};

const styles = {
  container: {
    display: 'flex',
    flexDirection: 'column',
    alignItems: 'center',
    justifyContent: 'center',
    padding: '40px 20px',
    backgroundColor: '#ffffff',
    border: '1px solid #e0e0e0',
    borderRadius: '10px',
    maxWidth: '1200px',
    margin: '0 auto',
    fontFamily: 'Arial, sans-serif',
    boxShadow: '0 4px 8px rgba(0, 0, 0, 0.1)',
    textAlign: 'center', // Center-align all text
  },
  content: {
    maxWidth: '800px',
    marginBottom: '40px', // Space between text and image
  },
  heading: {
    fontSize: '36px',
    fontWeight: 'bold',
    textTransform: 'uppercase',
    marginBottom: '20px',
  },
  quality: {
    color: '#28a745', // Green color for "QUALITY"
    display: 'block', // Ensure "QUALITY" is on top
  },
  assurance: {
    color: '#fd7e14', // Orange color for "ASSURANCE"
    display: 'block', // Ensure "ASSURANCE" is below
  },
  paragraph: {
    fontSize: '18px',
    color: '#555555',
    lineHeight: '1.8',
    marginBottom: '30px',
  },
  readMoreLink: {
    display: 'inline-block',
    padding: '12px 24px',
    backgroundColor: '#fd7e14', // Orange background for the button
    color: '#ffffff',
    textDecoration: 'none',
    borderRadius: '5px',
    fontSize: '16px',
    fontWeight: '600',
    transition: 'background-color 0.3s ease',
  },
  imageContainer: {
    maxWidth: '100%',
  },
  image: {
    maxWidth: '100%',
    height: 'auto',
    borderRadius: '10px',
  },
};

export default QualityAssurance;
