import React, { useState, useCallback, useEffect } from "react";
import { useNavigate } from "react-router-dom";
import backgroundImage from "./../../assets/global.jpg";

const AboutUs = () => {
  const [headerColor, setHeaderColor] = useState("orange");
  const [sidebarOpen, setSidebarOpen] = useState(false);
  const [isSqueezing, setIsSqueezing] = useState(false);
  const navigate = useNavigate();

  const handleScroll = useCallback(() => {
    requestAnimationFrame(() => {
      setHeaderColor(window.scrollY > 50 ? "orange" : "orange");
    });
  }, []);

  useEffect(() => {
    document.body.style.overflowX = "hidden";
    document.documentElement.style.overflowX = "hidden";
    window.addEventListener("scroll", handleScroll);
    return () => {
      window.removeEventListener("scroll", handleScroll);
      document.body.style.overflowX = "";
      document.documentElement.style.overflowX = "";
    };
  }, [handleScroll]);

  const toggleSidebar = () => {
    if (sidebarOpen) {
      setSidebarOpen(false);
      setIsSqueezing(false);
    } else {
      setIsSqueezing(true);
      setTimeout(() => {
        setSidebarOpen(true);
      }, 1000);
    }
  };

  useEffect(() => {
    if (sidebarOpen || isSqueezing) {
      document.body.style.overflow = "hidden";
    } else {
      document.body.style.overflow = "";
    }
  }, [sidebarOpen, isSqueezing]);

  return (
    <div
      className={`page-wrapper ${isSqueezing ? "squeezing" : ""} ${sidebarOpen ? "sidebar-active" : ""}`}
      style={{
        backgroundImage: `url(${backgroundImage})`,
        backgroundSize: "cover",
        backgroundPosition: "center",
        minHeight: "100vh",
      }}
    >
      <header
        className={`main-header ${isSqueezing ? "header-squeezing" : ""} ${sidebarOpen ? "header-shifted" : ""}`}
        style={{
          position: "fixed",
          top: 0,
          left: 0,
          right: 0,
          backgroundColor: headerColor,
          zIndex: 1000,
          padding: "10px 15px",
          height: "80px",
          display: "flex",
          alignItems: "center",
          justifyContent: "space-between",
          width: "100%",
        }}
      >
        <div style={{ fontSize: "20px", fontWeight: "bold", color: "white" }}>
          Madzenga Consulting
        </div>
      </header>
      <div style={{ paddingTop: "100px", textAlign: "center", color: "white" }}>
        <h1>About Us</h1>
        <p>Welcome to Madzenga Consulting, your trusted partner in environmental management.</p>
      </div>
    </div>
  );
};

export default AboutUs;
