import React from 'react';
import { mdiBriefcase, mdiFolder } from '@mdi/js';
import Icon from '@mdi/react';

const Portfolio = () => {
  return (
    <>
      <section className="portfolio-section">
        <div className="portfolio-content">
          <div className="portfolio-text">
            <h1 style={{ color: 'white', fontSize: '3rem' }}>Our Portfolio</h1>
            <p className="section-subtitle" style={{ color: 'white', fontSize: '1.5rem' }}>Showcasing Our Best Projects</p>
          </div>
          <div className="portfolio-cards">
            <div className="portfolio-card">
              <Icon path={mdiBriefcase} size={2} color="white" />
              <h2 style={{ color: '#5cb6f9', fontSize: '1.5rem' }}>Project 1: Giyani Market App</h2>
              <img src={require('./../../assets/app.png')} alt="Giyani Market App" style={{ width: '100%', borderRadius: '10px' }} />
              <p style={{ color: 'white', fontSize: '1.5rem', textAlign: 'center' }}>
                The Giyani Market app is designed to enhance the shopping experience by providing users with real-time access to local market listings, special offers, and product availability.
              </p>
              <ul style={{ color: 'white', fontSize: '1.5rem' }}>
                <li>User-Friendly Interface</li>
                <li>Real-Time Updates</li>
                <li>Secure Payment Options</li>
              </ul>
              <button style={{ backgroundColor: '#01224a', color: 'white', border: 'none', padding: '10px 20px', borderRadius: '10px' }}>View Case Study</button>
            </div>
            <div className="portfolio-card">
              <Icon path={mdiFolder} size={2} color="white" />
              <h2 style={{ color: '#5cb6f9', fontSize: '1.5rem' }}>Project 2: Roomigle Website</h2>
              <img src={require('./../../assets/web.png')} alt="Roomigle Website" style={{ width: '100%', borderRadius: '10px' }} />
              <p style={{ color: 'white', fontSize: '1.5rem', textAlign: 'center' }}>
                Roomigle is an accommodation website that simplifies the booking process, offering users a seamless experience for finding and reserving accommodations.
              </p>
              <ul style={{ color: 'white', fontSize: '1.5rem' }}>
                <li>Intuitive Booking System</li>
                <li>Responsive Design</li>
                <li>User Reviews and Ratings</li>
              </ul>
              <button style={{ backgroundColor: '#01224a', color: 'white', border: 'none', padding: '10px 20px', borderRadius: '10px' }}>View Case Study</button>
            </div>
          </div>
        </div>
      </section>

      <style jsx>{`
        .portfolio-section {
          padding: 100px 0;
          background-color: #00183d; 
        }

        .portfolio-content {
          max-width: 1200px;
          margin: 0 auto;
          padding: 0 20px;
        }

        .portfolio-text {
          margin-bottom: 40px;
          text-align: center;
        }

        .portfolio-cards {
          display: flex;
          flex-wrap: wrap;
          justify-content: center;
          align-items: stretch; /* Ensure cards are aligned */
        }

        .portfolio-card {
          background-color: #01224a;
          padding: 20px;
          border-radius: 10px;
          box-shadow: 0 0 10px rgba(0, 0, 0, 0.1);
          width: calc(30% - 40px); /* Adjust width for spacing */
          margin: 20px;
          display: flex;
          flex-direction: column; /* Stack content vertically */
          align-items: center; /* Center items */
        }

        .portfolio-card h2 {
          margin-top: 20px;
        }

        .portfolio-card ul {
          list-style: none;
          padding: 0;
          margin: 0;
        }

        .portfolio-card li {
          margin-bottom: 10px;
          color: white;
        }

        /* Media Queries for Mobile */
        @media (max-width: 768px) {
          .portfolio-section {
            padding: 50px 0;
          }

          .portfolio-content {
            padding: 0 10px;
          }

          .portfolio-text h1 {
            font-size: 2.5rem;
          }

          .portfolio-text p {
            font-size: 1.2rem;
          }

          .portfolio-cards {
            flex-direction: column;
            align-items: center;
          }

          .portfolio-card {
            width: 90%; /* Full width for mobile */
            margin: 10px 0; /* Vertical margin only */
          }

          .portfolio-card h2 {
            font-size: 1.3rem;
          }

          .portfolio-card p, .portfolio-card ul li {
            font-size: 1.2rem;
          }

          .portfolio-card button {
            padding: 8px 16px;
            font-size: 1rem;
          }
        }
      `}</style>
    </>
  );
};

export default Portfolio;
