import React, { useEffect } from 'react';

const ContactForm = () => {
  useEffect(() => {
    const contactInfo = document.querySelector('.contact-info');
    const formSection = document.querySelector('.form-section');

    setTimeout(() => {
      contactInfo.classList.add('fade-in');
      formSection.classList.add('zoom-in');
    }, 100);
  }, []);

  return (
    <>
      <section className="contact-section">
        <div className="contact-header">
          <h1>Contact Us</h1>
          <p>We'd love to hear from you! Whether you have a question, comment, or concern, please don't hesitate to reach out.</p>
        </div>

        <div className="contact-content">
          <div className="contact-info">
            <div className="info-item">
              <div className="icon-circle">
                <svg xmlns="http://www.w3.org/2000/svg" className="icon" fill="none" viewBox="0 0 24 24" stroke="currentColor">
                  <path strokeLinecap="round" strokeLinejoin="round" strokeWidth={2} d="M16 7a4 4 0 11-8 0 4 4 0 018 0zM12 14a7 7 0 00-7 7h14a7 7 0 00-7-7z" />
                </svg>
              </div>
              <div className="info-text">
                <p>Hlayiseka T Mabunda â€“ Editorial & Sales Director</p>
              </div>
            </div>

            <div className="info-item">
              <div className="icon-circle">
                <svg xmlns="http://www.w3.org/2000/svg" className="icon" fill="none" viewBox="0 0 24 24" stroke="currentColor">
                  <path strokeLinecap="round" strokeLinejoin="round" strokeWidth={2} d="M3 5a2 2 0 012-2h3.28a1 1 0 01.948.684l1.498 4.493a1 1 0 01-.502 1.21l-2.257 1.13a11.042 11.042 0 005.516 5.516l1.13-2.257a1 1 0 011.21-.502l4.493 1.498a1 1 0 01.684.949V19a2 2 0 01-2 2h-1C9.716 21 3 14.284 3 6V5z" />
                </svg>
              </div>
              <div className="info-text">
                <p>Phone: 073 594 7166</p>
              </div>
            </div>

            <div className="info-item">
              <div className="icon-circle">
                <svg xmlns="http://www.w3.org/2000/svg" className="icon" fill="none" viewBox="0 0 24 24" stroke="currentColor">
                  <path strokeLinecap="round" strokeLinejoin="round" strokeWidth={2} d="M3 8l7.89 5.26a2 2 0 002.22 0L21 8M5 19h14a2 2 0 002-2V7a2 2 0 00-2-2H5a2 2 0 00-2 2v10a2 2 0 002 2z" />
                </svg>
              </div>
              <div className="info-text">
                <p>Email:</p>
              </div>
            </div>

            <div className="info-item">
              <div className="icon-circle">
                <svg xmlns="http://www.w3.org/2000/svg" className="icon" fill="none" viewBox="0 0 24 24" stroke="currentColor">
                  <path strokeLinecap="round" strokeLinejoin="round" strokeWidth={2} d="M21 12a9 9 0 01-9 9m9-9a9 9 0 00-9-9m9 9H3m9 9a9 9 0 01-9-9m9 9c1.657 0 3-4.03 3-9s-1.343-9-3-9m0 18c-1.657 0-3-4.03-3-9s1.343-9 3-9m-9 9a9 9 0 019-9" />
                </svg>
              </div>
              <div className="info-text">
                <p>Website: <a href="#" className="website-link">Flair Magazine ZA</a></p>
              </div>
            </div>
          </div>

          <div className="form-section">
            <form>
              <div className="form-group">
                <label htmlFor="fullName">Full Name</label>
                <input type="text" id="fullName" />
              </div>

              <div className="form-group">
                <label htmlFor="email">Email</label>
                <input type="email" id="email" />
              </div>

              <div className="form-group">
                <label htmlFor="message">Message</label>
                <textarea id="message" rows="3"></textarea>
              </div>

              <div className="submit-container">
                <button type="submit" className="submit-button">Submit</button>
              </div>
            </form>
          </div>
        </div>
      </section>

      <style jsx>{`
        .contact-section {
          width: 100%;
          padding: 0;
          overflow: hidden;
        }

        .contact-header {
          background-color: #8e24aa;
          color: white;
          padding: 40px 20px;
          text-align: left;
        }

        .contact-header h1 {
          font-size: 4rem;
          font-weight: bold;
          margin: 0 0 10px 0;
        }

        .contact-header p {
          font-size: 1.1rem;
          max-width: 800px;
          margin: 0;
        }

        .contact-content {
          display: flex;
          justify-content: space-between;
          max-width: 1200px;
          margin: 0 auto;
          padding: 40px 20px;
        }

        .contact-info {
          flex: 1;
          opacity: 0;
          animation: fadeInText 1.5s forwards;
        }

        .info-item {
          display: flex;
          align-items: center;
          margin-bottom: 20px;
        }

        .icon-circle {
          width: 60px;
          height: 60px;
          border-radius: 50%;
          background-color: black;
          display: flex;
          justify-content: center;
          align-items: center;
          margin-right: 15px;
        }

        .icon {
          width: 30px;
          height: 30px;
          color: white;
        }

        .info-text {
          flex: 1;
        }

        .info-text p {
          margin: 0;
          font-size: 1.1rem;
        }

        .website-link {
          color: black;
          text-decoration: underline;
        }

        .form-section {
          flex: 1;
          padding: 0 0 0 40px;
          opacity: 0;
          transform: scale(0.95);
          animation: zoomIn 1.5s forwards;
        }

        .form-group {
          margin-bottom: 25px;
        }

        .form-group label {
          display: block;
          font-size: 1.5rem;
          font-weight: bold;
          margin-bottom: 8px;
        }

        .form-group input,
        .form-group textarea {
          width: 100%;
          border: none;
          border-bottom: 2px solid #333;
          padding: 8px 0;
          font-size: 1rem;
          background: transparent;
          outline: none;
        }

        .submit-container {
          display: flex;
          justify-content: flex-end;
          margin-top: 30px;
        }

        .submit-button {
          background-color: #0f1624;
          color: white;
          font-size: 1.2rem;
          padding: 12px 40px;
          border: none;
          border-radius: 4px;
          cursor: pointer;
        }

        .fade-in {
          animation: fadeInText 1.5s forwards;
        }

        .zoom-in {
          animation: zoomIn 1.5s forwards;
        }

        @keyframes fadeInText {
          to { opacity: 1; }
        }

        @keyframes zoomIn {
          to { opacity: 1; transform: scale(1); }
        }

        @media (max-width: 768px) {
          .contact-content {
            flex-direction: column;
          }

          .contact-info {
            margin-bottom: 40px;
          }

          .form-section {
            padding: 0;
          }

          .contact-header h1 {
            font-size: 2.5rem;
          }
        }
      `}</style>
    </>
  );
};

export default ContactForm;
