import React from "react";
import { Button } from "@/components/ui/button";
import { Card, CardContent } from "@/components/ui/card";
import { motion } from "framer-motion";
import { CheckCircle } from "lucide-react";

const HomePage = () => {
  return (
    <div className="min-h-screen bg-gray-100">
      {/* Hero Section */}
      <div className="text-center py-20 bg-blue-600 text-white">
        <motion.h1 className="text-5xl font-bold" initial={{ opacity: 0 }} animate={{ opacity: 1 }} transition={{ duration: 1 }}>
          Empowering Your Digital Vision
        </motion.h1>
        <p className="text-lg mt-4 max-w-2xl mx-auto">
          We specialize in cutting-edge web and mobile development to bring your ideas to life.
        </p>
        <Button className="mt-6 bg-white text-blue-600 px-6 py-3 rounded-lg font-semibold shadow-lg hover:bg-gray-200">
          Get in Touch
        </Button>
      </div>

      {/* Showcase Section */}
      <div className="py-16 px-6 max-w-6xl mx-auto">
        <h2 className="text-3xl font-bold text-center text-gray-800">Our Services</h2>
        <div className="grid md:grid-cols-3 gap-6 mt-8">
          {[
            { title: "Web Development", desc: "High-performance and scalable web solutions." },
            { title: "Mobile Apps", desc: "Custom iOS and Android applications." },
            { title: "UI/UX Design", desc: "Modern and user-friendly designs." },
          ].map((service, index) => (
            <Card key={index} className="shadow-md hover:shadow-lg transition p-6 text-center">
              <CardContent>
                <h3 className="text-xl font-semibold text-blue-600">{service.title}</h3>
                <p className="text-gray-700 mt-2">{service.desc}</p>
              </CardContent>
            </Card>
          ))}
        </div>
      </div>

      {/* Why Choose Us Section */}
      <div className="py-16 bg-gray-200 px-6">
        <h2 className="text-3xl font-bold text-center text-gray-800">Why Choose Us?</h2>
        <div className="max-w-4xl mx-auto mt-8 grid md:grid-cols-3 gap-6">
          {[
            "Expert Team with Years of Experience",
            "Innovative Solutions Tailored for Your Needs",
            "Customer-Centric Approach Ensuring Satisfaction",
          ].map((point, index) => (
            <div key={index} className="flex items-center space-x-3">
              <CheckCircle className="text-blue-600" size={24} />
              <p className="text-gray-700 font-medium">{point}</p>
            </div>
          ))}
        </div>
      </div>
    </div>
  );
};

export default HomePage;
