    setShowContactModal(true);
  };

  const closeModal = () => {
    setShowContactModal(false);
  };

  return (
    <>
      <section className="hero-section">
        <div className="hero-content">
          <div className="hero-text">
            <p className="welcome-text">Welcome to</p>
