            <h1 className="company-name">Media (Pty) Ltd</h1>
            <p className="company-description">We are a premier communication consultancy providing expert media services, content creation, and public relations solutions to a diverse portfolio of clients.</p>
            <div className="hero-button-container">
              <button onClick={handleContactClick}>Get in touch</button>
            </div>
          </div>
          <div className="hero-image">
            <img src={require('./../../assets/laptop.png')} alt="Newspaper and laptop" />
          </div>
        </div>
      </section>

      {showContactModal && (
        <div className="modal-overlay">
          <div className="modal">
            <h3>Contact us via:</h3>
            <div className="modal-buttons">
              <a className="modal-button" href="https://wa.me/27780211698" target="_blank" rel="noopener noreferrer">WhatsApp</a>
              <a className="modal-button" href="mailto:blessie999@gmail.com">Email</a>
            </div>
            <button className="close-modal" onClick={closeModal}>Close</button>
          </div>
        </div>
