import React from 'react';

const ContactUs = () => {
  return (
    <>

      <section className="contact-us-section">
        <div className="contact-us-content">
          <div className="contact-us-text">
            <h1 style={{ color: 'white' }}>Get in Touch</h1>
            <p className="section-subtitle" style={{ color: 'white' }}>Contact Us for Any Queries or Feedback</p>
          </div>
          <div className="contact-us-form">
            <form>
              <input type="text" placeholder="Name" style={{ padding: '10px', margin: '10px 0', width: '100%' }} />
              <input type="email" placeholder="Email" style={{ padding: '10px', margin: '10px 0', width: '100%' }} />
              <textarea placeholder="Message" style={{ padding: '10px', margin: '10px 0', width: '100%' }} />
              <button type="submit" style={{ backgroundColor: '#01224a', color: 'white', border: 'none', padding: '10px 20px', borderRadius: '10px' }}>Send Message</button>
            </form>
          </div>
          <div className="contact-us-info">
            <h2 style={{ color: 'white' }}>Location & Address</h2>
            <p style={{ color: 'white' }}>
              Mabunda Tech
              <br />
              123 Main St, Anytown, USA
              <br />
              <a href="https://www.google.com/maps/place/Anytown,+USA" target="_blank" style={{ color: 'white' }}>View on Google Maps</a>
            </p>
            <h2 style={{ color: 'white' }}>Phone & Email</h2>
            <p style={{ color: 'white' }}>
              Phone: 555-555-5555
              <br />
              Email: <a href="mailto:info@mabundatech.com" style={{ color: 'white' }}>info@mabundatech.com</a>
            </p>
            <h2 style={{ color: 'white' }}>Social Media Links</h2>
            <p style={{ color: 'white' }}>
              <a href="https://www.linkedin.com/company/mabundatech" target="_blank" style={{ color: 'white' }}>LinkedIn</a>
              <br />
              <a href="https://twitter.com/mabundatech" target="_blank" style={{ color: 'white' }}>Twitter</a>
              <br />
              <a href="https://github.com/mabundatech" target="_blank" style={{ color: 'white' }}>GitHub</a>
            </p>
          </div>
        </div>
      </section>

      <style jsx>{`
        .contact-us-section {
          padding: 100px 0;
          background-color: #00183d; 
        }

        .contact-us-content {
          max-width: 1200px;
          margin: 0 auto;
          padding: 0 20px;
          display: flex;
          flex-wrap: wrap;
          justify-content: space-between;
        }

        .contact-us-text {
          margin-bottom: 40px;
          text-align: center;
          width: 100%;
        }

        .contact-us-form {
          width: calc(50% - 20px);
          margin: 20px;
          padding: 20px;
          background-color: #01224a;
          border-radius: 10px;
          box-shadow: 0 0 10px rgba(0, 0, 0, 0.1);
        }

        .contact-us-info {
          width: calc(50% - 20px);
          margin: 20px;
          padding: 20px;
          background-color: #01224a;
          border-radius: 10px;
          box-shadow: 0 0 10px rgba(0, 0, 0, 0.1);
        }
      `}</style>
    </>
  );
};

export default ContactUs;
