import React from 'react';

const QualityAssurance = () => {
  return (
    <div style={styles.container}>
      <div style={styles.textContainer}>
        <h1 style={styles.heading}>QUALITY ASSURANCE</h1>
        <p style={styles.paragraph}>
          We are committed to meeting the needs of our customers, staff and stakeholders to ensure that services are carried out to client satisfaction and maintain the integrity and sustainability of the environment.
        </p>
        <a href="/read-more" style={styles.readMoreLink}>Read More</a>
      </div>
      <div style={styles.imageContainer}>
        <img
          src="https://via.placeholder.com/400x300" // Replace with your image URL
          alt="Quality Assurance"
          style={styles.image}
        />
      </div>
    </div>
  );
};

const styles = {
  container: {
    display: 'flex',
    alignItems: 'center',
    justifyContent: 'space-between',
    padding: '40px 20px',
    backgroundColor: '#ffffff',
    border: '1px solid #e0e0e0',
    borderRadius: '10px',
    maxWidth: '1200px',
    margin: '0 auto',
    fontFamily: 'Arial, sans-serif',
    boxShadow: '0 4px 8px rgba(0, 0, 0, 0.1)',
  },
  textContainer: {
    flex: 1,
    paddingRight: '40px',
    textAlign: 'left',
  },
  heading: {
    fontSize: '28px',
    color: '#333333',
    marginBottom: '20px',
    fontWeight: 'bold',
    textTransform: 'uppercase',
  },
  paragraph: {
    fontSize: '18px',
    color: '#555555',
    lineHeight: '1.8',
    marginBottom: '30px',
  },
  readMoreLink: {
    display: 'inline-block',
    padding: '12px 24px',
    backgroundColor: '#007bff',
    color: '#ffffff',
    textDecoration: 'none',
    borderRadius: '5px',
    fontSize: '16px',
    fontWeight: '600',
    transition: 'background-color 0.3s ease',
  },
  imageContainer: {
    flex: 1,
    textAlign: 'right',
  },
  image: {
    maxWidth: '100%',
    height: 'auto',
    borderRadius: '10px',
  },
};

export default QualityAssurance;
