import React from 'react';

const ContactUs = () => {
  const handlePhoneClick = () => {
    window.location.href = 'tel:0780211698';
  };

  const handleEmailClick = () => {
    window.location.href = 'mailto:blessie999@gmail.com';
  };

  const handleAddressClick = async (event) => {
    event.preventDefault(); // Prevent default behavior if used in an <a> tag

    const address = encodeURIComponent('362 Laydes Street, Pretoria');
    const mapsUrl = `https://www.google.com/maps/search/?api=1&query=${address}`;

    try {
      await navigator.clipboard.writeText('362 Laydes Street, Pretoria');
      window.open(mapsUrl, '_blank', 'noopener,noreferrer');
    } catch (err) {
      console.error('Failed to copy address:', err);
    }
  };

  return (
    <>
      <section className="contact-us-section">
        <div className="contact-us-content">
          <div className="contact-us-header">
            <h1 className="contact-title">Get in Touch</h1>
            <p className="contact-subtitle">Contact us for any queries or support.</p>
          </div>
          <div className="contact-us-right">
            <h2 className="contact-info-title">Contact Information</h2>
            <div className="contact-info">
              <p className="contact-detail" onClick={handleAddressClick} style={{ cursor: 'pointer' }}>
                Address: 362 Laydes Street, Pretoria
              </p>
              <p className="contact-detail" onClick={handlePhoneClick} style={{ cursor: 'pointer' }}>
                Phone: 078 021 1698
              </p>
              <p className="contact-detail">
                Email: <a href="#" onClick={handleEmailClick} className="contact-email">blessie999@gmail.com</a>
              </p>
            </div>
          </div>
        </div>
      </section>

      <style jsx>{`
        .contact-us-section {
          padding: 10px 20px;
          background-color: #00183d;
        }

        .contact-us-content {
          max-width: 1200px;
          margin: 0 auto;
          display: flex;
          flex-direction: column;
          justify-content: center;
          align-items: center;
        }

        .contact-us-header {
          text-align: center;
          margin-bottom: 40px;
        }

        .contact-title {
          color: #5cb6f9;
          font-size: 4rem;
          margin-bottom: 20px;
        }

        .contact-subtitle {
          color: white;
          font-size: 1.5rem;
          margin-bottom: 40px;
        }

        .contact-us-right {
          width: 100%;
          margin: 10px 0;
          padding: 30px;
          background-color: #01224a;
          border-radius: 10px;
          box-shadow: 0 4px 20px rgba(0, 0, 0, 0.2);
          min-height: 400px;
          display: flex;
          flex-direction: column;
        }

        .contact-info-title {
          color: #5cb6f9;
          font-size: 2rem;
          margin-bottom: 10px;
        }

        .contact-info {
          margin-top: 5px;
        }

        .contact-detail {
          color: white;
          font-size: 1.5rem;
          margin-bottom: 15px;
        }

        .contact-detail:hover {
          text-decoration: underline;
        }

        .contact-email {
          color: white;
          text-decoration: underline;
        }

        .contact-us-right:hover {
          box-shadow: 0 6px 30px rgba(0, 0, 0, 0.3);
        }
      `}</style>
    </>
  );
};

export default ContactUs;
