import React, { useEffect, useState, useCallback } from "react";
import HeroSection from "../../components/landingpage/HeroSection.js";
import AboutUs from "../../components/landingpage/AboutUs";
import Services from "../../components/landingpage/Services";
import Quality from "../../components/landingpage/QualityAssurance.js";
import Testimonials from "../../components/landingpage/Testimonials";
import ContactUs from "../../components/landingpage/ContactUs";
import Table from "../../components/landingpage/table.js";
import Powered from "../../components/landingpage/powered.js";
import FAQ from "../../components/landingpage/FAQ";
import { useNavigate } from "react-router-dom";

const LandingPage = () => {
  const [headerColor, setHeaderColor] = useState("green");
  const [headerHeight, setHeaderHeight] = useState("80px");
  const [headerShadow, setHeaderShadow] = useState("none");
  const [sidebarOpen, setSidebarOpen] = useState(false);
  const [pageScale, setPageScale] = useState(1);
  const [pageBlur, setPageBlur] = useState(0);
  const [isTransitioning, setIsTransitioning] = useState(false);

  const handleScroll = useCallback(() => {
    requestAnimationFrame(() => {
      if (window.scrollY > 50) {
        setHeaderColor("rgba(0, 128, 0, 0.95)"); // Slightly transparent green
        setHeaderHeight("60px"); // Smaller header
        setHeaderShadow("0 4px 12px rgba(0, 0, 0, 0.15)"); // Add shadow when scrolling
      } else {
        setHeaderColor("green");
        setHeaderHeight("80px");
        setHeaderShadow("none");
      }
    });
  }, []);

  const navigate = useNavigate();

  const handleNavigation = (e, path) => {
    e.preventDefault();

    // Apply page transition effect
    setIsTransitioning(true);
    setPageScale(0.95);
    setPageBlur(2);

    setTimeout(() => {
      navigate(path);
      setPageScale(1);
      setPageBlur(0);
      setTimeout(() => {
        setIsTransitioning(false);
      }, 300);
    }, 300);
  };

  const scrollToSection = (id) => {
    // Apply page transition effect first
    setIsTransitioning(true);
    setPageScale(0.95);
    setPageBlur(2);

    setTimeout(() => {
      const element = document.getElementById(id);
      if (element) {
        element.scrollIntoView({ behavior: "smooth", block: "start" });
        // Close sidebar after clicking a link on mobile
        setSidebarOpen(false);
      }

      // Reset the page transition effect
      setTimeout(() => {
        setPageScale(1);
        setPageBlur(0);
        setIsTransitioning(false);
      }, 300);
    }, 300);
  };

  useEffect(() => {
    document.body.style.overflowX = "hidden";
    document.documentElement.style.overflowX = "hidden";

    window.addEventListener("scroll", handleScroll);
    return () => {
      window.removeEventListener("scroll", handleScroll);
      document.body.style.overflowX = "";
      document.documentElement.style.overflowX = "";
    };
  }, [handleScroll]);

  return (
    <div style={{ width: "100vw", overflowX: "hidden" }}>
      {/* Header Section */}
      <header
        style={{
          position: "fixed",
          top: 0,
          left: 0,
          right: 0,
          backgroundColor: headerColor,
          zIndex: 1000,
          padding: "10px 15px",
          height: headerHeight,
          display: "flex",
          alignItems: "center",
          justifyContent: "space-between",
          transition: "all 0.4s cubic-bezier(0.165, 0.84, 0.44, 1)", // Smooth easing transition
          width: "100%",
          boxShadow: headerShadow,
        }}
      >
        {/* Company Name in place of the logo */}
        <div className="company-logo" style={{ fontSize: "20px", fontWeight: "bold", color: "white" }}>
          Madzenga Consulting
        </div>

        {/* Desktop Navigation - only visible on desktop */}
        <nav className="desktop-nav">
          <ul className="nav-list">
            <li>
              <a
                href="#about-us"
                onClick={(e) => {
                  e.preventDefault();
                  scrollToSection("about-us");
                }}
                className="nav-link"
                style={{ color: "white" }}
              >
                About Us
              </a>
            </li>
            <li>
              <a
                href="#services"
                onClick={(e) => {
                  e.preventDefault();
                  scrollToSection("services");
                }}
                className="nav-link"
                style={{ color: "white" }}
              >
                Services
              </a>
            </li>
            <li>
              <a
                href="/proofreading-services"
                onClick={(e) => {
                  e.preventDefault();
                  scrollToSection("proofreading");
                }}
                className="nav-link"
                style={{ color: "white" }}
              >
                Projects
              </a>
            </li>
            <li>
              <a
                href="/proofreading-services"
                onClick={(e) => {
                  e.preventDefault();
                  scrollToSection("proofreading");
                }}
                className="nav-link"
                style={{ color: "white" }}
              >
                Quality Assurance
              </a>
            </li>
            <li>
              <a
                href="#"
                onClick={(e) => {
                  e.preventDefault();
                  handleNavigation(e, "/get-started");
                }}
                className="nav-link-button"
                style={{ color: "white" }}
              >
                Get Started
              </a>
            </li>
          </ul>
        </nav>

        {/* Mobile menu hamburger icon - only visible on mobile */}
        <div className="mobile-menu-icon" onClick={() => setSidebarOpen(!sidebarOpen)}>
          <div className={`hamburger ${sidebarOpen ? "open" : ""}`}>
            <span></span>
            <span></span>
            <span></span>
          </div>
        </div>
      </header>

      {/* Mobile Sidebar - only visible on mobile when toggled */}
      <div className={`mobile-sidebar ${sidebarOpen ? 'open' : ''}`}>
        <div style={{ display: "flex", justifyContent: "center", padding: "0px", marginTop: "-50px" }}>
          <img
            src={require("./../../assets/logo.png")}
            alt="Logo"
            style={{ height: "60px", width: "60px" }}
          />
        </div>
        <div className="sidebar-close" onClick={() => setSidebarOpen(false)}>
          ✕
        </div>
        <ul className="sidebar-nav-list" style={{ marginTop: "10px" }}>
          <li>
            <a href="#about-us" onClick={(e) => { e.preventDefault(); scrollToSection("about-us"); }} className="sidebar-nav-link">
              About Us
            </a>
          </li>
          <li>
            <a href="#services" onClick={(e) => { e.preventDefault(); scrollToSection("services"); }} className="sidebar-nav-link">
              Services
            </a>
          </li>
          <li>
            <a href="/proofreading-services" onClick={(e) => { e.preventDefault(); scrollToSection("proofreading"); }} className="sidebar-nav-link">
              Projects
            </a>
          </li>
          <li>
            <a href="/proofreading-services" onClick={(e) => { e.preventDefault(); scrollToSection("proofreading"); }} className="sidebar-nav-link">
              Quality Assurance
            </a>
          </li>
          <li>
            <a href="" className="sidebar-nav-button" target="_blank" rel="noopener noreferrer">
              Get Started
            </a>
          </li>
        </ul>
      </div>

      {/* Overlay for when sidebar is open */}
      {sidebarOpen && <div className="sidebar-overlay" onClick={() => setSidebarOpen(false)}></div>}

      {/* Page Content with transitions */}
      <div 
        style={{ 
          paddingTop: "80px", 
          width: "100vw", 
          overflowX: "hidden",
          transform: `scale(${pageScale})`,
          filter: `blur(${pageBlur}px)`,
          transition: "all 0.4s cubic-bezier(0.165, 0.84, 0.44, 1)",
          transformOrigin: "center center"
        }}
      >
        <HeroSection />
        <div id="about-us"><AboutUs /></div>
        <Services />
        <Quality />
        <ContactUs />
        <br />
      </div>

      {/* Global Styles */}
      <style>
        {`
          * {
            margin: 0;
