import React from 'react';
import HeroSection from '../../components/landingpage/HeroSection.js';
import AboutUs from './../../components/landingpage/AboutUs';
import Services from './../../components/landingpage/Services';
import Portfolio from './../../components/landingpage/Portfolio';
import Testimonials from './../../components/landingpage/Testimonials';
import ContactUs from './../../components/landingpage/ContactUs';
import FAQ from './../../components/landingpage/FAQ';
// import "./../../css/landingPage.css";

const LandingPage = () => {
  return (
    <div>
      <header style={{ 
        position: 'fixed', 
        top: 0, 
        left: 0, 
        right: 0, 
        backgroundColor: '#00183d', 
        zIndex: 1000, 
        padding: '10px 20px', 
        height: '80px', // Increased height
        display: 'flex', 
        alignItems: 'center', 
        justifyContent: 'space-between' 
      }}>
        <img src={require('./../../assets/logo.png')} alt="Logo" style={{ height: '50px' }} />
        <nav>
          <ul style={{ display: 'flex', listStyleType: 'none', margin: 0, padding: 0 }}>
            <li style={{ marginRight: '30px' }}>
              <a 
                href="#about-us" 
                style={{ 
                  color: 'white', 
                  textDecoration: 'none', 
                  fontWeight: 'bold', 
                  transition: 'color 0.3s', 
                }}
                onMouseEnter={(e) => e.currentTarget.style.color = '#FFD700'} // Gold color on hover
                onMouseLeave={(e) => e.currentTarget.style.color = 'white'} // Reset color
              >
                About Us
              </a>
            </li>
            <li style={{ marginRight: '30px' }}>
              <a 
                href="#services" 
                style={{ 
                  color: 'white', 
                  textDecoration: 'none', 
                  fontWeight: 'bold', 
                  transition: 'color 0.3s', 
                }}
                onMouseEnter={(e) => e.currentTarget.style.color = '#FFD700'}
                onMouseLeave={(e) => e.currentTarget.style.color = 'white'}
              >
                Services
              </a>
            </li>
            <li>
              <a 
                href="#portfolio" 
                style={{ 
                  color: 'white', 
                  textDecoration: 'none', 
                  fontWeight: 'bold', 
                  transition: 'color 0.3s', 
                }}
                onMouseEnter={(e) => e.currentTarget.style.color = '#FFD700'}
                onMouseLeave={(e) => e.currentTarget.style.color = 'white'}
              >
                Portfolio
              </a>
            </li>
          </ul>
        </nav>
      </header>

      <div style={{ paddingTop: '100px' }}> {/* Increased padding to prevent content from hiding behind the header */}
        <HeroSection />

        <div id="about-us">
          <AboutUs />
        </div>
        <div id="services">
          <Services />
        </div>
        <div id="portfolio">
          <Portfolio />
        </div>

        <Testimonials />
        <ContactUs />
        <FAQ />
      </div>
    </div>
  );
};

export default LandingPage;
