import React, { useEffect, useState, useCallback } from "react";
import HeroSection from "../../components/landingpage/HeroSection.js";
import AboutUs from "../../components/landingpage/AboutUs";
import Services from "../../components/landingpage/Services";
import Quality from "../../components/landingpage/QualityAssurance.js";
import Testimonials from "../../components/landingpage/Testimonials";
import ContactUs from "../../components/landingpage/ContactUs";
import Table from "../../components/landingpage/table.js";
import Powered from "../../components/landingpage/powered.js";
import FAQ from "../../components/landingpage/FAQ";
import { useNavigate } from "react-router-dom";

const LandingPage = () => {
  const [headerColor, setHeaderColor] = useState("green");
  const [headerHeight, setHeaderHeight] = useState("80px");
  const [headerShadow, setHeaderShadow] = useState("none");
  const [sidebarOpen, setSidebarOpen] = useState(false);
  const [pageScale, setPageScale] = useState(1);
  const [pageBlur, setPageBlur] = useState(0);
  const [isTransitioning, setIsTransitioning] = useState(false);
  const [isScrolled, setIsScrolled] = useState(false);

  const handleScroll = useCallback(() => {
    requestAnimationFrame(() => {
      if (window.scrollY > 50) {
        setHeaderColor("rgba(0, 128, 0, 0.95)"); // Slightly transparent green
        setHeaderHeight("60px"); // Smaller header
        setHeaderShadow("0 4px 12px rgba(0, 0, 0, 0.15)"); // Add shadow when scrolling
        setIsScrolled(true); // Set scrolled state to true
      } else {
        setHeaderColor("green");
        setHeaderHeight("80px");
        setHeaderShadow("none");
        setIsScrolled(false); // Set scrolled state to false
      }
    });
  }, []);

  const navigate = useNavigate();

  const handleNavigation = (e, path) => {
    e.preventDefault();

    // Apply page transition effect
    setIsTransitioning(true);
    setPageScale(0.95);
    setPageBlur(2);

    setTimeout(() => {
      navigate(path);
      setPageScale(1);
      setPageBlur(0);
      setTimeout(() => {
        setIsTransitioning(false);
      }, 300);
    }, 300);
  };

  const scrollToSection = (id) => {
    // Apply page transition effect first
    setIsTransitioning(true);
    setPageScale(0.95);
    setPageBlur(2);

    setTimeout(() => {
      const element = document.getElementById(id);
      if (element) {
        element.scrollIntoView({ behavior: "smooth", block: "start" });
        // Close sidebar after clicking a link on mobile
        setSidebarOpen(false);
      }

      // Reset the page transition effect
      setTimeout(() => {
        setPageScale(1);
        setPageBlur(0);
        setIsTransitioning(false);
      }, 300);
    }, 300);
  };

  useEffect(() => {
    document.body.style.overflowX = "hidden";
    document.documentElement.style.overflowX = "hidden";

    window.addEventListener("scroll", handleScroll);
    return () => {
      window.removeEventListener("scroll", handleScroll);
      document.body.style.overflowX = "";
      document.documentElement.style.overflowX = "";
