import React, { useEffect, useState } from 'react';

const HeroSection = () => {
  const [showContactOptions, setShowContactOptions] = useState(false);

  useEffect(() => {
    const heroText = document.querySelector('.hero-text');
    const heroButton = document.querySelector('.hero-button-container');
    const heroImage = document.querySelector('.hero-image');

    setTimeout(() => {
      heroText.classList.add('fade-in');
      heroButton.classList.add('slide-up');
      heroImage.classList.add('zoom-in');
    }, 100);
  }, []);

  const handleContactClick = () => {
    setShowContactOptions(!showContactOptions);
  };

  return (
    <>
      <section className="hero-section">
        <div className="hero-content">
          <div className="hero-text">
            <div className="hero-title">
              <h1 style={{ display: 'inline', marginRight: '5px' }}>Empowering Innovation,</h1>
              <h1 style={{ display: 'inline', color: '#02f7ee' }}>One Line of Code at a Time.</h1>
            </div>
            <p>Transforming ideas into innovative software solutions, empowering businesses to thrive in a digital world.</p>
            <div className="hero-button-container">
              <button onClick={handleContactClick}>Let's Build Something</button>
            </div>
            {showContactOptions && (
              <div className="contact-options">
                <p>Contact us via:</p>
                <a href="https://wa.me/your-whatsapp-number" target="_blank" rel="noopener noreferrer">WhatsApp</a><br />
                <a href="mailto:your-email@example.com">Email</a>
              </div>
            )}
          </div>
          <div className="hero-image">
            <img src={require('./../../assets/code.jpg')} alt="Code" />
          </div>
        </div>
      </section>

      <style jsx>{`
        .hero-section {
          background-color: #00183d;
          padding: 80px 0;
          color: white;
          display: flex;
          justify-content: center;
          align-items: center;
          min-height: 60vh;
        }

        .hero-content {
          display: flex;
          justify-content: space-between;
          align-items: center;
          max-width: 1200px;
          margin: 0 auto;
          opacity: 0;
          animation: fadeIn 2s forwards;
        }

        .hero-text {
          flex-basis: 50%;
          text-align: center;
        }

        .hero-image {
          flex-basis: 50%;
          text-align: center;
          overflow: hidden;
          box-shadow: 0 0 20px rgba(0, 0, 0, 0.4);
          opacity: 0;
          transform: scale(0.9);
          animation: zoomIn 2s forwards;
        }

        .hero-image img {
          width: 100%;
          height: auto;
          object-fit: cover;
          border-radius: 20px;
        }

        .hero-text h1 {
          font-size: 3rem;
          opacity: 0;
          animation: fadeInText 1.5s forwards;
          animation-delay: 0.5s;
        }

        .hero-text h1:nth-child(2) {
          animation-delay: 1s;
        }

        .hero-text p {
          font-size: 1.5rem;
          margin-bottom: 30px;
          opacity: 0;
          animation: fadeInText 2s forwards;
          animation-delay: 1.5s;
        }

        .hero-text button {
          background-color: #02f7ee;
          color: #00183d;
          padding: 15px 30px;
          border: none;
          font-size: 1.2rem;
          cursor: pointer;
          border-radius: 5px;
        }

        .hero-text button:hover {
          background-color: #007bff;
        }

        .hero-button-container {
          margin-top: 30px;
          opacity: 0;
          transform: translateY(20px);
          animation: slideUp 1s forwards;
          animation-delay: 2s;
        }

        .contact-options {
          margin-top: 20px;
          text-align: left;
        }

        .contact-options a {
          color: #02f7ee;
          text-decoration: none;
        }

        .contact-options a:hover {
          text-decoration: underline;
        }

        @keyframes fadeIn {
          to { opacity: 1; }
        }

        @keyframes fadeInText {
          to { opacity: 1; }
        }

        @keyframes zoomIn {
          to { opacity: 1; transform: scale(1); }
        }

        @keyframes slideUp {
          to { opacity: 1; transform: translateY(0); }
        }

        @media (max-width: 768px) {
          .hero-content {
            flex-direction: column;
            text-align: center;
          }

          .hero-text {
            flex-basis: 100%;
            margin-bottom: 20px;
          }

          .hero-image {
            flex-basis: 100%;
          }

          .hero-image img {
            width: 100%;
          }

          .hero-text h1 {
            font-size: 2rem;
          }

          .hero-text p {
            font-size: 1.2rem;
          }

          .hero-text button {
            font-size: 1rem;
            padding: 12px 24px;
          }
        }
      `}</style>
    </>
  );
};

export default HeroSection;
