import React, { useState, useEffect } from 'react';
import { Menu, X } from 'lucide-react';

const LandingPage = () => {
  const [headerColor, setHeaderColor] = useState('#00183d');
  const [isMenuOpen, setIsMenuOpen] = useState(false);

  const handleScroll = () => {
    const scrollTop = window.scrollY;
    setHeaderColor(scrollTop > 50 ? '#002d55' : '#00183d');
  };

  useEffect(() => {
    window.addEventListener('scroll', handleScroll);
    return () => window.removeEventListener('scroll', handleScroll);
  }, []);

  const scrollToSection = (id) => {
    const element = document.getElementById(id);
    if (element) {
      element.scrollIntoView({ behavior: 'smooth' });
    }
    setIsMenuOpen(false);
  };

  const navItems = [
    { id: 'about-us', label: 'About Us' },
    { id: 'services', label: 'Services' },
    { id: 'portfolio', label: 'Portfolio' },
  ];

  return (
    <div>
      <header 
        className="fixed top-0 left-0 right-0 z-50 transition-colors duration-300"
        style={{ backgroundColor: headerColor }}
      >
        <div className="px-4 py-2 flex items-center justify-between">
          <img 
            src={require('./../../assets/logo.png')} 
            alt="Logo" 
            className="h-12 w-auto"
          />

          {/* Mobile Menu Button */}
          <button 
            className="md:hidden p-2 text-white"
            onClick={() => setIsMenuOpen(!isMenuOpen)}
          >
            {isMenuOpen ? <X size={24} /> : <Menu size={24} />}
          </button>

          {/* Desktop Navigation */}
          <nav className="hidden md:block">
            <ul className="flex space-x-6">
              {navItems.map(item => (
                <li key={item.id}>
                  <a
                    href={`#${item.id}`}
                    onClick={(e) => {
                      e.preventDefault();
                      scrollToSection(item.id);
                    }}
                    className="text-white hover:text-yellow-400 transition-colors duration-300 font-bold text-lg"
                  >
                    {item.label}
                  </a>
                </li>
              ))}
            </ul>
          </nav>
        </div>

        {/* Mobile Navigation Menu */}
        {isMenuOpen && (
          <nav className="md:hidden bg-opacity-95" style={{ backgroundColor: headerColor }}>
            <ul className="px-4 py-2">
              {navItems.map(item => (
                <li key={item.id} className="py-2 border-b border-gray-700 last:border-0">
                  <a
                    href={`#${item.id}`}
                    onClick={(e) => {
                      e.preventDefault();
                      scrollToSection(item.id);
                    }}
                    className="block text-white hover:text-yellow-400 transition-colors duration-300 font-medium text-base"
                  >
                    {item.label}
                  </a>
                </li>
              ))}
            </ul>
          </nav>
        )}
      </header>

      <div className="pt-24">
        <HeroSection />
        <div id="about-us"><AboutUs /></div>
        <div id="services"><Services /></div>
        <div id="portfolio"><Portfolio /></div>
        <Testimonials />
        <ContactUs />
        <FAQ />
      </div>
    </div>
  );
};

export default LandingPage;
