import React from 'react';

const Footer = () => {
  return (
    <div className="page-wrapper">
      <footer id="contact" className="footer">
        <div className="footer-content">
          <p className="footer-text">&copy; 2018 Madzenga Consulting. Powered by Mabunda Tech</p>
          <p className="contact-info">
            Contact us at{' '}
            <a href="mailto:blessie999@gmail.com" className="footer-link">blessie999@gmail.com</a>
          </p>
        </div>
      </footer>

      <style jsx>{`
        .page-wrapper {
          background-color: #ff6600;  /* Orange background for the entire page */
          color: white;
          min-height: 100vh;  /* Ensures the footer is at the bottom if content is less */
          display: flex;
          flex-direction: column;
        }

        .footer {
          padding: 20px;
          text-align: center;
          width: 100%;
          font-family: Arial, sans-serif;
          margin-top: auto; /* Pushes the footer to the bottom of the page */
        }

        .footer-content {
          display: flex;
          flex-direction: column;
          align-items: center;
          justify-content: center;
        }

        .footer-text {
          font-size: 14px;
          font-weight: bold;
          margin: 0;
          padding: 5px 0;
        }

        .contact-info {
          font-size: 12px;
          margin: 5px 0;
        }

        .footer-link {
          color: white;
          text-decoration: none;
          font-weight: bold;
          transition: color 0.3s;
        }

        .footer-link:hover {
          color: #ffcc00;  /* Light yellow for hover effect */
        }
      `}</style>
    </div>
  );
};

export default Footer;
