import React, { useEffect, useState, useCallback } from "react";
import HeroSection from "../../components/landingpage/HeroSection.js";
import AboutUs from "../../components/landingpage/AboutUs";
import Services from "../../components/landingpage/Services";
import Portfolio from "../../components/landingpage/Portfolio";
import Testimonials from "../../components/landingpage/Testimonials";
import ContactUs from "../../components/landingpage/ContactUs";
import FAQ from "../../components/landingpage/FAQ";

const LandingPage = () => {
  const [headerColor, setHeaderColor] = useState("#00183d");

  // Optimized Scroll Handler with useCallback
  const handleScroll = useCallback(() => {
    requestAnimationFrame(() => {
      const scrollTop = window.scrollY;
      setHeaderColor(scrollTop > 50 ? "#002d55" : "#00183d");
    });
  }, []);

  useEffect(() => {
    window.addEventListener("scroll", handleScroll);
    return () => window.removeEventListener("scroll", handleScroll);
  }, [handleScroll]);

  const scrollToSection = (id) => {
    setTimeout(() => {
      const element = document.getElementById(id);
      if (element) {
        element.scrollIntoView({ behavior: "smooth", block: "start" });
      }
    }, 100);
  };

  return (
    <div>
      {/* Header Section */}
      <header
        style={{
          position: "fixed",
          top: 0,
          left: 0,
          right: 0,
          backgroundColor: headerColor,
          zIndex: 1000,
          padding: "10px 15px",
          height: "70px",
          display: "flex",
          alignItems: "center",
          justifyContent: "space-between",
          transition: "background-color 0.3s ease",
        }}
      >
        <img
          src={require("./../../assets/logo.png")}
          alt="Logo"
          style={{ height: "60px" }}
        />
        <nav>
          <ul className="nav-list">
            <li>
              <a href="#about-us" onClick={(e) => { e.preventDefault(); scrollToSection("about-us"); }} className="nav-link">
                About Us
              </a>
            </li>
            <li>
              <a href="#services" onClick={(e) => { e.preventDefault(); scrollToSection("services"); }} className="nav-link">
                Services
              </a>
            </li>
            <li>
              <a href="#portfolio" onClick={(e) => { e.preventDefault(); scrollToSection("portfolio"); }} className="nav-link">
                Portfolio
              </a>
            </li>
          </ul>
        </nav>
      </header>

      {/* Page Content */}
      <div style={{ paddingTop: "90px" }}>
        <HeroSection />
        <div id="about-us"><AboutUs /></div>
        <div id="services"><Services /></div>
        <div id="portfolio"><Portfolio /></div>
        <Testimonials />
        <ContactUs />
        <FAQ />
      </div>

      {/* Global Styles */}
      <style>
        {`
          .nav-list {
            display: flex;
            list-style-type: none;
            margin: 0;
            padding: 0;
            gap: 12px;
          }

          .nav-link {
            color: white;
            text-decoration: none;
            font-weight: bold;
            font-size: 18px;
            transition: color 0.3s;
          }

          .nav-link:hover {
            color: #FFD700;
          }

          @media (max-width: 768px) {
            .nav-link {
              font-size: 13px !important;
            }
          }
        `}
      </style>
    </div>
  );
};

export default LandingPage;
