import React, { useEffect, useState, useCallback } from "react";
import HeroSection from "../../components/landingpage/HeroSection.js";
import AboutUs from "../../components/landingpage/AboutUs";
import Services from "../../components/landingpage/Services";
import Portfolio from "../../components/landingpage/Portfolio";
import Testimonials from "../../components/landingpage/Testimonials";
import ContactUs from "../../components/landingpage/ContactUs";
import FAQ from "../../components/landingpage/FAQ";

const LandingPage = () => {
  const [headerColor, setHeaderColor] = useState("#89298d");
  const [isMobile, setIsMobile] = useState(false); // State for mobile view
  const [dropdownOpen, setDropdownOpen] = useState(false); // State for dropdown toggle

  const handleScroll = useCallback(() => {
    requestAnimationFrame(() => {
      setHeaderColor(window.scrollY > 50 ? "#561959" : "#89298d");
    });
  }, []);

  const handleResize = () => {
    setIsMobile(window.innerWidth <= 768); // Check if screen is mobile
  };

  useEffect(() => {
    document.body.style.overflowX = "hidden";
    document.documentElement.style.overflowX = "hidden";

    window.addEventListener("scroll", handleScroll);
    window.addEventListener("resize", handleResize);
    handleResize(); // Initialize on load

    return () => {
      window.removeEventListener("scroll", handleScroll);
      window.removeEventListener("resize", handleResize);
      document.body.style.overflowX = "";
      document.documentElement.style.overflowX = "";
    };
  }, [handleScroll]);

  const scrollToSection = (id) => {
    setTimeout(() => {
      const element = document.getElementById(id);
      if (element) {
        element.scrollIntoView({ behavior: "smooth", block: "start" });
      }
    }, 100);
  };

  const toggleDropdown = () => {
    setDropdownOpen(!dropdownOpen); // Toggle dropdown visibility
  };

  return (
    <div style={{ width: "100vw", overflowX: "hidden" }}>
      {/* Header Section */}
      <header
        style={{
          position: "fixed",
          top: 0,
          left: 0,
          right: 0,
          backgroundColor: headerColor,
          zIndex: 1000,
          padding: "10px 15px",
          height: "110px",
          display: "flex",
          alignItems: "center",
          justifyContent: "space-between",
          transition: "background-color 0.3s ease",
          width: "100%",
        }}
      >
        {/* Logo */}
        <div className="logo-container" style={{
          width: '100px',
          height: '60px',
          borderRadius: '25px',
          backgroundColor: 'white',
          display: 'flex',
          justifyContent: 'center',
          alignItems: 'center',
          padding: '5px',
          border: '2px solid white',
          boxShadow: '0 0 8px rgba(138, 43, 226, 0.4)',
          overflow: 'hidden',
        }}>
          <img
            src={require("./../../assets/logo.png")}
            alt="Logo"
            style={{ height: "45px", width: "45px" }}
          />
        </div>

        {/* Navigation */}
        <nav>
          {isMobile ? (
            // Mobile Dropdown
            <div>
              <button onClick={toggleDropdown} className="dropdown-toggle">Menu</button>
              {dropdownOpen && (
                <ul className="dropdown-menu">
                  <li>
                    <a href="#about-us" onClick={(e) => { e.preventDefault(); scrollToSection("about-us"); }} className="nav-link">
                      About Us
                    </a>
                  </li>
                  <li>
                    <a href="#services" onClick={(e) => { e.preventDefault(); scrollToSection("services"); }} className="nav-link">
                      Our Services
                    </a>
                  </li>
                  <li>
                    <a href="#proofreading" onClick={(e) => { e.preventDefault(); scrollToSection("proofreading"); }} className="nav-link">
                      Proofreading Services
                    </a>
                  </li>
                  <li>
                    <a href="https://flairconcepts.co.za" className="nav-link-button" target="_blank" rel="noopener noreferrer">
                      The Flair Magazine ZA
                    </a>
                  </li>
                </ul>
              )}
            </div>
          ) : (
            // Desktop Navigation
            <ul className="nav-list">
              <li>
                <a href="#about-us" onClick={(e) => { e.preventDefault(); scrollToSection("about-us"); }} className="nav-link">
                  About Us
                </a>
              </li>
              <li>
                <a href="#services" onClick={(e) => { e.preventDefault(); scrollToSection("services"); }} className="nav-link">
                  Our Services
                </a>
              </li>
              <li>
                <a href="#proofreading" onClick={(e) => { e.preventDefault(); scrollToSection("proofreading"); }} className="nav-link">
                  Proofreading Services
                </a>
              </li>
              <li>
                <a href="https://flairconcepts.co.za" className="nav-link-button" target="_blank" rel="noopener noreferrer">
                  The Flair Magazine ZA
                </a>
              </li>
            </ul>
          )}
        </nav>
      </header>

      {/* Page Content */}
      <div style={{ paddingTop: "50px", width: "100vw", overflowX: "hidden" }}>
        <HeroSection />
        <div id="about-us"><AboutUs /></div>
        <div id="services"><Services /></div>
        <div id="proofreading"><Portfolio /></div>
        <Testimonials />
        <ContactUs />
      </div>

      {/* Global Styles */}
      <style>
        {`
          * {
            margin: 0;
            padding: 0;
            box-sizing: border-box;
            overflow-x: hidden;
          }

          body, html {
            width: 100%;
            overflow-x: hidden;
          }

          .nav-list {
            display: flex;
            list-style-type: none;
            margin: 0;
            padding: 0;
            gap: 60px;
            align-items: center;
          }

          .nav-link {
            color: white;
            text-decoration: none;
            font-weight: normal;
            font-size: 18px;
            transition: color 0.3s;
          }

          .nav-link:hover {
            color: #FFD700;
          }

          .nav-link-button {
            color: #000000;
            background-color: white;
            text-decoration: none;
            font-weight: bold;
            font-size: 16px;
            padding: 12px 20px;
            border-radius: 8px;
            box-shadow: 0 4px 6px rgba(0, 0, 0, 0.1);
            display: inline-block;
            text-align: center;
            transition: all 0.3s;
          }

          .nav-link-button:hover {
            background-color: #8A2BE2;
            color: white;
            cursor: pointer;
            box-shadow: 0 6px 10px rgba(0, 0, 0, 0.2);
          }

          /* Mobile Dropdown Styles */
          .dropdown-toggle {
            background-color: transparent;
            border: none;
            color: white;
            font-size: 18px;
            cursor: pointer;
          }

          .dropdown-menu {
            position: absolute;
            background-color: #89298d;
            top: 50px;
            right: 0;
            list-style-type: none;
            padding: 10px;
            margin: 0;
            border-radius: 5px;
            box-shadow: 0 4px 6px rgba(0, 0, 0, 0.2);
          }

          .dropdown-menu li {
            padding: 8px 0;
          }

          .dropdown-menu a {
            color: white;
            text-decoration: none;
            font-size: 16px;
          }

          .dropdown-menu a:hover {
            color: #FFD700;
          }

          @media (max-width: 768px) {
            .nav-link, .nav-link-button {
              font-size: 13px !important;
            }

            .nav-link-button {
              padding: 5px 10px;
            }

            .nav-list {
              gap: 10px;
            }
          }
        `}
      </style>
    </div>
  );
};

export default LandingPage;
