import React from 'react';

const Testimonials = () => {
  return (
    <>

      <section className="testimonials-section">
        <div className="testimonials-content">
          <div className="testimonials-text">
            <h1 style={{ color: 'white' ,fontSize: '3rem'}}>What Our Clients Say</h1>
            <p className="section-subtitle" style={{ color: 'white',fontSize: '1.5rem' }}>Testimonials and Reviews</p>
          </div>
          <div className="testimonials-cards">
            <div className="testimonials-card">
              <p style={{ color: 'white' }}>
                "Mabunda Tech has been a game-changer for our business. Their mobile app development services are top-notch and their team is very professional."
              </p>
              <h3 style={{ color: 'white' }}>John Doe, CEO of XYZ Inc.</h3>
            </div>
            <div className="testimonials-card">
              <p style={{ color: 'white' }}>
                "We were blown away by the quality of work delivered by Mabunda Tech. Their web development services are exceptional and we highly recommend them."
              </p>
              <h3 style={{ color: 'white' }}>Jane Smith, Marketing Manager of ABC Corp.</h3>
            </div>
            {/* <div className="testimonials-card">
              <p style={{ color: 'white' }}>
                "Mabunda Tech's team is very responsive and easy to work with. They delivered our project on time and exceeded our expectations."
              </p>
              <h3 style={{ color: 'white' }}>Bob Johnson, Founder of Startup XYZ</h3>
            </div> */}
          </div>
        </div>
      </section>

      <style jsx>{`
        .testimonials-section {
          padding: 100px 0;
          background-color: #00183d; 
        }

        .testimonials-content {
          max-width: 1200px;
          margin: 0 auto;
          padding: 0 20px;
        }

        .testimonials-text {
          margin-bottom: 40px;
          text-align: center;
        }

        .testimonials-cards {
          display: flex;
          flex-wrap: wrap;
          justify-content: center;
        }

        .testimonials-card {
          background-color: #01224a;
          padding: 20px;
          border-radius: 10px;
          box-shadow: 0 0 10px rgba(0, 0, 0, 0.1);
          width: calc(30% - 20px);
          margin: 20px;
        }

        .testimonials-card p {
          font-size: 1.2rem;
          margin-bottom: 20px;
          color: white;
        }

        .testimonials-card h3 {
          font-size: 1.5rem;
          margin-top: 0;
          color: white;
        }
      `}</style>
    </>
  );
};

export default Testimonials;
