import React, { useEffect, useState } from 'react';
import HeroSection from '../../components/landingpage/HeroSection.js';
import AboutUs from './../../components/landingpage/AboutUs';
import Services from './../../components/landingpage/Services';
import Portfolio from './../../components/landingpage/Portfolio';
import Testimonials from './../../components/landingpage/Testimonials';
import ContactUs from './../../components/landingpage/ContactUs';
import FAQ from './../../components/landingpage/FAQ';

const LandingPage = () => {
  const [headerColor, setHeaderColor] = useState('#00183d');

  // Debounced Scroll Handler
  const handleScroll = () => {
    const scrollTop = window.scrollY;
    if (scrollTop > 50) {
      setHeaderColor('#002d55');
    } else {
      setHeaderColor('#00183d');
    }
  };

  useEffect(() => {
    // Debounce the scroll event to optimize performance
    const debouncedScroll = () => {
      requestAnimationFrame(handleScroll);
    };

    window.addEventListener('scroll', debouncedScroll);
    return () => {
      window.removeEventListener('scroll', debouncedScroll);
    };
  }, []);

  const scrollToSection = (id) => {
    const element = document.getElementById(id);
    if (element) {
      element.scrollIntoView({ behavior: 'smooth' });
    }
  };

  const navLinkStyle = {
    color: 'white',
    textDecoration: 'none',
    fontWeight: 'bold',
    fontSize: '18px',
    transition: 'color 0.3s',
  };

  return (
    <div>
      <header 
        style={{ 
          position: 'fixed', 
          top: 0, 
          left: 0, 
          right: 0, 
          backgroundColor: headerColor, 
          zIndex: 1000, 
          padding: '10px 15px', 
          height: '70px', 
          display: 'flex', 
          alignItems: 'center', 
          justifyContent: 'space-between',
          willChange: 'transform',  // Optimizing performance
          transition: 'background-color 0.3s ease' // Smooth transition for color
        }}
      >
        <img 
          src={require('./../../assets/logo.png')} 
          alt="Logo" 
          style={{ height: '60px' }} 
        />
        <nav>
          <ul style={{ 
            display: 'flex', 
            listStyleType: 'none', 
            margin: 0, 
            padding: 0,
            gap: '12px' 
          }}>
            <li>
              <a 
                href="#about-us" 
                onClick={(e) => { e.preventDefault(); scrollToSection('about-us'); }} 
                className="nav-link"
                style={navLinkStyle}
                onMouseEnter={(e) => e.currentTarget.style.color = '#FFD700'}
                onMouseLeave={(e) => e.currentTarget.style.color = 'white'}
              >
                About Us
              </a>
            </li>
            <li>
              <a 
                href="#services" 
                onClick={(e) => { e.preventDefault(); scrollToSection('services'); }} 
                className="nav-link"
                style={navLinkStyle}
                onMouseEnter={(e) => e.currentTarget.style.color = '#FFD700'}
                onMouseLeave={(e) => e.currentTarget.style.color = 'white'}
              >
                Services
              </a>
            </li>
            <li>
              <a 
                href="#portfolio" 
                onClick={(e) => { e.preventDefault(); scrollToSection('portfolio'); }} 
                className="nav-link"
                style={navLinkStyle}
                onMouseEnter={(e) => e.currentTarget.style.color = '#FFD700'}
                onMouseLeave={(e) => e.currentTarget.style.color = 'white'}
              >
                Portfolio
              </a>
            </li>
          </ul>
        </nav>
      </header>

      <div style={{ paddingTop: '90px' }}>
        <HeroSection />
        <div id="about-us"><AboutUs /></div>
        <div id="services"><Services /></div>
        <div id="portfolio"><Portfolio /></div>
        <Testimonials />
        <ContactUs />
        <FAQ />
      </div>

      <style>
        {`
          @media (max-width: 768px) {
            .nav-link {
              font-size: 13px !important;
            }
          }
        `}
      </style>
    </div>
  );
};

export default LandingPage;
