import React from 'react';

const SustainabilityHero = () => {
  // Inline CSS styles
  const styles = {
    heroContainer: {
      display: 'flex',
      flexDirection: 'row',
      width: '100%',
      maxWidth: '1200px',
      margin: '0 auto',
      padding: '20px',
      gap: '40px',
      alignItems: 'center',
      backgroundImage: "url(" + require("./../../assets/global.jpg") + ")",
      backgroundSize: 'cover',
      backgroundPosition: 'center',
      borderRadius: '8px',
    },
    heroContent: {
      flex: 1,
      backgroundColor: 'rgba(255, 255, 255, 0.8)', // Adds contrast for readability
      padding: '20px',
      borderRadius: '8px',
    },
    logoContainer: {
      marginBottom: '24px',
    },
    companyName: {
      color: 'green',
      fontWeight: 800,
      marginBottom: '8px',
      textAlign: 'center',
    },
    mainHeading: {
      fontSize: '42px',
      fontWeight: 700,
      color: '#111827',
      marginBottom: '16px',
      lineHeight: 1.2,
      textAlign: 'center',
    },
    coloredHeading: {
      marginBottom: '24px',
      fontSize: '42px',
      fontWeight: 700,
      lineHeight: 1.2,
      textAlign: 'center',
    },
    greenText: {
       color: 'green',
    },
    orangeText: {
      color: '#fb923c',
    },
    description: {
      color: '#4b5563',
      marginBottom: '32px',
      maxWidth: '450px',
      lineHeight: 1.5,
      textAlign: 'center',
      marginLeft: 'auto',
      marginRight: 'auto',
    },
    ctaButton: {
      backgroundColor: '#fb923c',
      color: 'white',
      fontWeight: 700,
      padding: '12px 24px',
      borderRadius: '8px',
      border: 'none',
      cursor: 'pointer',
      transition: 'background-color 0.3s',
    },
    heroImageContainer: {
      flex: 1,
    },
    heroImage: {
      height: '500px',
      width: '500px',
      borderRadius: '20px',
    },
  };

  return (
    <div style={styles.heroContainer}>
      <div style={styles.heroContent}>
        <div style={styles.logoContainer}>
          <img src={require("./../../assets/logo.png")} alt="Logo" style={{ height: "85px", width: "85px" }} />
        </div>
        <p style={styles.companyName}>We Are Madzenga Consulting</p>
        <h1 style={styles.mainHeading}>Building a Greener</h1>
        <h1 style={styles.mainHeading}>Economy Through</h1>
        <div style={styles.coloredHeading}>
          <span style={styles.greenText}>Sustainable </span>
          <span style={styles.orangeText}>Solutions</span>
        </div>
        <p style={styles.description}>
          Ensuring compliance, innovation, and environmental stewardship for a sustainable future.
        </p>
        <div style={{ display: 'flex', justifyContent: 'center', alignItems: 'center' }}>
          <button 
            style={styles.ctaButton}
            onMouseOver={(e) => e.currentTarget.style.backgroundColor = '#f97316'} 
            onMouseOut={(e) => e.currentTarget.style.backgroundColor = '#fb923c'}
          >
            Quality Assurance
          </button>
        </div>
      </div>
      {/* <div style={styles.heroImageContainer}>
        <img src={require("./../../assets/heropage.png")} alt="Hero" style={styles.heroImage} />
      </div> */}
    </div>
  );
};

export default SustainabilityHero;
