import React from 'react';

const Services = () => {
  return (
    <div style={{ fontFamily: 'Arial, sans-serif', padding: '20px', maxWidth: '1200px', margin: '0 auto' }}>
      <h1 style={{ fontSize: '24px', color: '#333', marginBottom: '20px', textAlign: 'center' }}>Our Services</h1>

      {/* Container for horizontal layout */}
      <div style={{ display: 'flex', justifyContent: 'space-between', gap: '20px' }}>
        {/* Service Sections */}
        {services.map((service, index) => (
          <div key={index} style={{ flex: 1, border: '1px solid #ddd', padding: '20px', borderRadius: '8px', textAlign: 'center' }}>
            <div style={{ display: 'flex', alignItems: 'center', justifyContent: 'center', marginBottom: '15px' }}>
              <img
                src={require(`./../../assets/${service.icon}`)}
                alt={`${service.title} Icon`}
                style={{ width: '50px', height: '50px', marginRight: '10px' }}
              />
              <h2 style={{ fontSize: '20px', color: '#555', margin: 0 }}>{service.title}</h2>
            </div>
            <ul style={{ listStyleType: 'none', paddingLeft: '20px', textAlign: 'left' }}>
              {service.items.map((item, idx) => (
                <li key={idx} style={{ fontSize: '16px', color: '#666', marginBottom: '10px', position: 'relative' }}>
                  <span style={{ position: 'absolute', left: '-20px', color: '#333' }}>â€¢</span>{item}
                </li>
              ))}
            </ul>
          </div>
        ))}
      </div>
    </div>
  );
};

const services = [
  {
    icon: '1.png',
    title: 'Environmental Authorisations',
    items: [
      'Environmental Impact Assessments',
      'Water Use Licence Applications (WULA) and Integrated Water and Waste Management Plans (IWWMP)',
      'EMP Development and Performance Assessment',
      'Rehabilitation and Closure Plans',
      'Financial Provision',
    ],
  },
  {
    icon: '2.png',
    title: 'Management System Solutions',
    items: [
      'Integrated EHS Management Systems',
      'Onsite EHS Compliance Officers/ Practitioners',
      'EHS System Audits and Training',
      'Leadership Training',
    ],
  },
  {
    icon: '3.png',
    title: 'EHS Legal Compliance Services',
    items: [
      'EHS Legal Compliance Audits',
      'Due Diligence Audits',
      'Compliance of Legal Registers',
      'Permitting Register',
      'Permitting Compliance Frameworks and Strategies',
      'ESG Compliance Audits',
      'ESG Compliance Frameworks and Strategies',
    ],
  },
];

export default Services;
