import React from 'react';

const HeroSection = () => {
  return (
    <>
      <section className="hero-section">
        <div className="hero-content">
          <div className="hero-text">
            <h1>Welcome to CodeCraft Co.</h1>
            <p>Transforming ideas into innovative software solutions, empowering businesses to thrive in a digital world.</p>
            <button>Let's Build Something</button>
          </div>
          <div className="hero-image">
            <img src={require('./../../assets/code.jpg')} />
          </div>
        </div>
      </section>

      <style jsx>{`
        .hero-section {
          background-color: #00183d; /* New background color */
          padding: 100px 0;
          color: white;
          display: flex;
          justify-content: center;
          align-items: center;
          height: 50vh;
        }

        .hero-content {
          display: flex;
          justify-content: space-between;
          align-items: center;
          max-width: 1200px;
          margin: 0 auto;
          padding: 0 20px;
        }

        .hero-text {
          flex-basis: 50%;
          text-align: center;
        }

        .hero-image {
          flex-basis: 50%;
          text-align: center;
          border-radius: 20px; /* Add slight curve to the image container */
          overflow: hidden; /* Hide any overflowing content */
          box-shadow: 0 0 10px rgba(0, 0, 0, 0.2); /* Add shadow */
        }

        .hero-image img {
          width: 100%;
          height: 100%;
          object-fit: cover; /* Ensure the image covers the entire container */
        }

        .hero-text h1 {
          font-size: 3rem;
          margin-bottom: 20px;
        }

        .hero-text p {
          font-size: 1.5rem;
          margin-bottom: 30px;
        }

        .hero-text button {
          background-color: #02f7ee;
          color: #00183d;
          padding: 15px 30px;
          border: none;
          font-size: 1.2rem;
          cursor: pointer;
          border-radius: 5px;
        }

        .hero-text button:hover {
          background-color: #007bff;
        }
      `}</style>
    </>
  );
};

export default HeroSection;
