import React, { useState, useEffect } from 'react';

const AnimatedSustainabilityHero = () => {
  const [isVisible, setIsVisible] = useState(false);
  const [isHovered, setIsHovered] = useState(false);
  const [dots, setDots] = useState([]);
  const [rotation, setRotation] = useState(0);

  // Generate floating dots for background animation
  useEffect(() => {
    setIsVisible(true);

    const generateDots = () => {
      const newDots = [];
      for (let i = 0; i < 20; i++) {
        newDots.push({
          id: i,
          left: Math.random() * 100,
          top: Math.random() * 100,
          size: Math.random() * 10 + 5,
          speed: Math.random() * 2 + 1,
          delay: Math.random() * 5,
          opacity: Math.random() * 0.5 + 0.2
        });
      }
      setDots(newDots);
    };

    generateDots();

    // Rotate logo
    const rotationInterval = setInterval(() => {
      setRotation(prev => (prev + 1) % 360);
    }, 50);

    return () => clearInterval(rotationInterval);
  }, []);

  const styles = {
    heroContainer: {
      position: 'relative',
      display: 'flex',
      flexDirection: 'row',
      width: '100%',
      maxWidth: '4xl',
      margin: '0 auto',
      padding: '6',
      gap: '10',
      alignItems: 'center',
      borderRadius: '2xl',
      overflow: 'hidden',
      backgroundColor: '#f0f9ff',
      boxShadow: '0 10px 25px -5px rgba(0, 0, 0, 0.1)',
      height: '32rem'
    },
    backgroundEffect: {
      position: 'absolute',
      width: '100%',
      height: '100%',
      background: 'linear-gradient(120deg, #e0f2fe 0%, #dcfce7 100%)',
      zIndex: '0'
    },
    heroContent: {
      position: 'relative',
      flex: '1',
      backgroundColor: 'rgba(255, 255, 255, 0.85)',
      padding: '6',
      borderRadius: '2xl',
      boxShadow: '0 4px 6px -1px rgba(0, 0, 0, 0.1)',
      transform: isVisible ? 'translateY(0)' : 'translateY(50px)',
      opacity: isVisible ? 1 : 0,
      transition: 'transform 0.8s ease-out, opacity 0.8s ease-out',
      zIndex: '10',
      margin: '4'
    },
    logoContainer: {
      display: 'flex',
      justifyContent: 'center',
      marginBottom: '6',
      transform: `rotate(${rotation}deg)`,
      transition: 'transform 0.3s ease'
    },
    logoPlaceholder: {
      height: '24',
      width: '24',
      borderRadius: '9999px',
      backgroundColor: 'green',
      display: 'flex',
      justifyContent: 'center',
      alignItems: 'center'
    },
    companyName: {
      color: 'green',
      fontWeight: 'bold',
      marginBottom: '2',
      textAlign: 'center',
      animation: isVisible ? 'fadeIn 1s ease-in-out' : '',
      opacity: isVisible ? 1 : 0,
      transition: 'opacity 1s ease-in-out 0.2s'
    },
    mainHeading: {
      fontSize: '4xl',
      fontWeight: 'bold',
      color: '#111827',
      marginBottom: '4',
      lineHeight: '1.2',
      textAlign: 'center',
      transform: isVisible ? 'translateX(0)' : 'translateX(-50px)',
      opacity: isVisible ? 1 : 0,
      transition: 'transform 0.6s ease-out 0.3s, opacity 0.6s ease-out 0.3s'
    },
    coloredHeading: {
      marginBottom: '6',
      fontSize: '4xl',
      fontWeight: 'bold',
      lineHeight: '1.2',
      textAlign: 'center',
      transform: isVisible ? 'translateX(0)' : 'translateX(50px)',
      opacity: isVisible ? 1 : 0,
      transition: 'transform 0.6s ease-out 0.5s, opacity 0.6s ease-out 0.5s'
    },
    greenText: {
      color: 'green',
      animation: isVisible ? 'colorPulse 3s infinite' : ''
    },
    orangeText: {
      color: '#fb923c',
      animation: isVisible ? 'wiggle 3s ease-in-out infinite' : ''
    },
    description: {
      color: '#4b5563',
      marginBottom: '8',
      maxWidth: 'md',
      lineHeight: '1.5',
      textAlign: 'center',
      marginLeft: 'auto',
      marginRight: 'auto',
      transform: isVisible ? 'translateY(0)' : 'translateY(20px)',
      opacity: isVisible ? 1 : 0,
      transition: 'transform 0.6s ease-out 0.7s, opacity 0.6s ease-out 0.7s'
    },
    ctaButton: {
      backgroundColor: isHovered ? '#f97316' : '#fb923c',
      color: 'white',
      fontWeight: 'bold',
      padding: '3 6',
      borderRadius: 'lg',
      border: 'none',
      cursor: 'pointer',
      transition: 'all 0.3s ease',
      transform: `scale(${isHovered ? 1.05 : 1})`,
      boxShadow: isHovered ? '0 10px 15px -3px rgba(0, 0, 0, 0.1)' : '0 4px 6px -1px rgba(0, 0, 0, 0.1)',
      animation: isVisible ? 'bounceIn 1s ease-out 0.9s both' : ''
    },
    buttonContainer: {
      display: 'flex',
      justifyContent: 'center',
      alignItems: 'center',
      opacity: isVisible ? 1 : 0,
      transition: 'opacity 0.6s ease-out 0.9s'
    },
    floatingDot: {
      position: 'absolute',
      borderRadius: '9999px',
      backgroundColor: 'rgba(5, 150, 105, 0.3)',
      zIndex: '1',
      animation: 'float 10s infinite linear'
    }
  };

  return (
    <div className="relative w-full max-w-4xl mx-auto p-6 rounded-2xl overflow-hidden shadow-xl" style={styles.heroContainer}>
      {/* Animated background */}
      <div className="absolute inset-0 bg-gradient-to-br from-blue-50 to-green-50" style={styles.backgroundEffect}>
        {dots.map(dot => (
          <div
            key={dot.id}
            className="absolute rounded-full"
            style={{
              left: `${dot.left}%`,
              top: `${dot.top}%`,
              width: `${dot.size}px`,
              height: `${dot.size}px`,
              backgroundColor: dot.id % 2 === 0 ? 'rgba(5, 150, 105, 0.3)' : 'rgba(251, 146, 60, 0.3)',
              animation: `float ${dot.speed * 10}s infinite linear ${dot.delay}s, pulse 3s infinite ease-in-out ${dot.delay}s`,
              opacity: dot.opacity
            }}
          />
        ))}
      </div>

      <div className="relative flex-1 bg-white bg-opacity-85 p-6 rounded-2xl shadow-md z-10 m-4 transform transition-all duration-800 ease-out" style={styles.heroContent}>
        <div className="flex justify-center mb-6 transition-transform duration-300" style={styles.logoContainer}>
          <div className="h-24 w-24 rounded-full bg-green-600 flex justify-center items-center text-white font-bold text-xl">MC</div>
        </div>

        <p className="text-green-600 font-bold mb-2 text-center opacity-0 animate-fadeIn" style={styles.companyName}>
          We Are Madzenga Consulting
        </p>

        <h1 className="text-4xl font-bold text-gray-900 mb-4 text-center transform transition-all duration-600 delay-300 ease-out" style={styles.mainHeading}>
          Building a Greener
          <br />
          Economy Through
        </h1>

        <div className="mb-6 text-4xl font-bold text-center transform transition-all duration-600 delay-500 ease-out" style={styles.coloredHeading}>
          <span className="text-green-600 animate-colorPulse" style={styles.greenText}>Sustainable </span>
          <span className="text-orange-400 animate-wiggle inline-block" style={styles.orangeText}>Solutions</span>
        </div>

        <p className="text-gray-600 mb-8 max-w-md mx-auto leading-relaxed text-center transform transition-all duration-600 delay-700 ease-out" style={styles.description}>
          Ensuring compliance, innovation, and environmental stewardship for a sustainable future.
        </p>

        <div className="flex justify-center items-center opacity-0 transition-opacity duration-600 delay-900" style={styles.buttonContainer}>
          <button 
            className="bg-orange-400 hover:bg-orange-500 text-white font-bold py-3 px-6 rounded-lg shadow-md transition-all duration-300 animate-bounceIn"
            style={styles.ctaButton}
            onMouseEnter={() => setIsHovered(true)}
            onMouseLeave={() => setIsHovered(false)}
          >
            Quality Assurance
          </button>
        </div>
      </div>
    </div>
  );
};

export default AnimatedSustainabilityHero;
