import React, { useEffect, useState, useCallback } from "react";
import HeroSection from "../../components/landingpage/HeroSection.js";
import AboutUs from "../../components/landingpage/AboutUs";
import Services from "../../components/landingpage/Services";
import Quality from "../../components/landingpage/QualityAssurance.js";
import Testimonials from "../../components/landingpage/Testimonials";
import ContactUs from "../../components/landingpage/ContactUs";
import Table from "../../components/landingpage/table.js";
import Powered from "../../components/landingpage/powered.js";
import FAQ from "../../components/landingpage/FAQ";
import Footer from "../../components/landingpage/Footer.js";
import { useNavigate } from "react-router-dom";

const LandingPage = () => {
  const [headerColor, setHeaderColor] = useState("green");
  const [sidebarOpen, setSidebarOpen] = useState(false);
  const [isSqueezing, setIsSqueezing] = useState(false);
  const [isContentHidden, setIsContentHidden] = useState(false);

  const handleScroll = useCallback(() => {
    requestAnimationFrame(() => {
      setHeaderColor(window.scrollY > 50 ? "green" : "green");
    });
  }, []);

  const navigate = useNavigate();
  const handleNavigation = (e) => {
    e.preventDefault();
    navigate("/proofreading-services");
  };

  useEffect(() => {
    document.body.style.overflowX = "hidden";
    document.documentElement.style.overflowX = "hidden";

    window.addEventListener("scroll", handleScroll);
    return () => {
      window.removeEventListener("scroll", handleScroll);
      document.body.style.overflowX = "";
      document.documentElement.style.overflowX = "";
    };
  }, [handleScroll]);

  const toggleSidebar = () => {
    if (sidebarOpen) {
      // Close sidebar immediately
      setSidebarOpen(false);
      setIsSqueezing(false);
      setIsContentHidden(false);
    } else {
      // Start squeezing animation
      setIsSqueezing(true);

      // After squeezing, hide the content
      setTimeout(() => {
        setIsContentHidden(true);
      }, 1000); // Matches the squeezing animation duration

      // After content is hidden, open the sidebar
      setTimeout(() => {
        setSidebarOpen(true);
      }, 1200); // Slight delay after content is hidden
    }
  };

  useEffect(() => {
    if (sidebarOpen || isSqueezing) {
      document.body.style.overflow = "hidden";
    } else {
      document.body.style.overflow = "";
    }
  }, [sidebarOpen, isSqueezing]);

  const scrollToSection = (id) => {
    setTimeout(() => {
      const element = document.getElementById(id);
      if (element) {
        element.scrollIntoView({ behavior: "smooth", block: "start" });
        setSidebarOpen(false);
        setIsSqueezing(false);
        setIsContentHidden(false);
      }
    }, 100);
  };

  return (
    <div className={`page-wrapper ${isSqueezing ? "squeezing" : ""} ${sidebarOpen ? "sidebar-active" : ""}`}>
      {/* Header Section */}
      <header
        className={`main-header ${isSqueezing ? "header-squeezing" : ""} ${sidebarOpen ? "header-shifted" : ""}`}
        style={{
          position: "fixed",
          top: 0,
          left: 0,
          right: 0,
          backgroundColor: headerColor,
          zIndex: 1000,
          padding: "10px 15px",
          height: "80px",
          display: "flex",
          alignItems: "center",
          justifyContent: "space-between",
          width: "100%",
        }}
      >
        <div style={{ fontSize: "20px", fontWeight: "bold", color: "white" }}>
          Madzenga Consulting
        </div>

        <nav className="desktop-nav">
          <ul className="nav-list">
            <li>
              <a
                href="#about-us"
                onClick={(e) => {
                  e.preventDefault();
                  scrollToSection("about-us");
                }}
                className="nav-link"
                style={{ color: "white" }}
              >
                About Us
              </a>
            </li>
            <li>
              <a
                href="#services"
                onClick={(e) => {
                  e.preventDefault();
                  scrollToSection("services");
                }}
                className="nav-link"
                style={{ color: "white" }}
              >
                Services
              </a>
            </li>
            <li>
              <a
                href="/proofreading-services"
                onClick={(e) => {
                  e.preventDefault();
                  scrollToSection("proofreading");
                }}
                className="nav-link"
                style={{ color: "white" }}
              >
                Projects
              </a>
            </li>
            <li>
              <a
                href="/proofreading-services"
                onClick={(e) => {
                  e.preventDefault();
                  scrollToSection("proofreading");
                }}
                className="nav-link"
                style={{ color: "white" }}
              >
                Quality Assurance
              </a>
            </li>
            <li>
              <a
                href="#"
                className="nav-link-button"
                target="_blank"
                rel="noopener noreferrer"
                style={{ color: "white" }}
              >
                Get Started
              </a>
            </li>
          </ul>
        </nav>

        <div className="mobile-menu-icon" onClick={toggleSidebar}>
          <div className={`hamburger ${sidebarOpen ? "open" : ""}`}>
            <span></span>
            <span></span>
            <span></span>
          </div>
        </div>
      </header>

      {/* Mobile Sidebar */}
      <div className={`mobile-sidebar ${sidebarOpen ? "open" : ""}`}>
        <div style={{ display: "flex", justifyContent: "center", padding: "0px", marginTop: "-50px" }}>
          <img
            src={require("./../../assets/logo.png")}
            alt="Logo"
            style={{ height: "60px", width: "60px" }}
          />
        </div>
        <div className="sidebar-close" onClick={toggleSidebar}>
          ✕
        </div>
        <ul className="sidebar-nav-list" style={{ marginTop: "10px" }}>
          <li>
            <a href="#about-us" onClick={(e) => { e.preventDefault(); scrollToSection("about-us"); }} className="sidebar-nav-link">
              About Us
            </a>
          </li>
          <li>
            <a href="#services" onClick={(e) => { e.preventDefault(); scrollToSection("services"); }} className="sidebar-nav-link">
              Services
            </a>
          </li>
          <li>
            <a href="/proofreading-services" onClick={(e) => { e.preventDefault(); scrollToSection("proofreading"); }} className="sidebar-nav-link">
              Projects
            </a>
          </li>
          <li>
            <a href="/proofreading-services" onClick={(e) => { e.preventDefault(); scrollToSection("proofreading"); }} className="sidebar-nav-link">
              Quality Assurance
            </a>
          </li>
          <li>
            <a href="" className="sidebar-nav-button" target="_blank" rel="noopener noreferrer">
              Get Started
            </a>
          </li>
        </ul>
      </div>

      {/* Overlay */}
      {(sidebarOpen || isSqueezing) && (
        <div className="sidebar-overlay" onClick={toggleSidebar}></div>
      )}

      {/* Page Content */}
      <div
        className={`page-content ${isSqueezing ? "content-squeezing" : ""} ${isContentHidden ? "content-hidden" : ""}`}
        style={{ paddingTop: "80px", width: "100vw", overflowX: "hidden" }}
      >
        <HeroSection />
        <div id="about-us"><AboutUs /></div>
        <Services />
        <Quality />
        <ContactUs />
        <Footer />
      </div>

      {/* Global Styles */}
      <style>
        {`
          * {
            margin: 0;
            padding: 0;
            box-sizing: border-box;
            overflow-x: hidden;
          }

          body, html {
            width: 100%;
            overflow-x: hidden;
          }

          .page-wrapper {
            position: relative;
