import React from 'react';

const Services = () => {
  return (
    <div style={{ fontFamily: 'Arial, sans-serif', padding: '20px', maxWidth: '1200px', margin: '0 auto' }}>
      <h1 style={{ fontSize: '24px', color: '#333', marginBottom: '40px', textAlign: 'center' }}>Our Services</h1>

      <div style={{ display: 'flex', justifyContent: 'center', gap: '20px', flexWrap: 'wrap' }}>
        {services.map((service, index) => (
          <div key={index} style={{
            flex: 1,
            border: '1px solid #ddd',
            padding: '20px',
            borderRadius: '10px',
            textAlign: 'center',
            position: 'relative', 
            background: 'white',
            boxShadow: '0px 4px 6px rgba(0, 0, 0, 0.1)',
            maxWidth: '350px',
            overflow: 'visible', // Ensures nothing is cut off
          }}>
            {/* Floating Icon */}
            <div style={{
              position: 'absolute',
              top: '-55px', // Move it further up to fully escape the border
              left: '50%',
              transform: 'translateX(-50%)',
              backgroundColor: '#fff', 
              padding: '15px',
              borderRadius: '50%',
              zIndex: 10, // Make sure it’s above everything
              border: '5px solid white', // Extra space around the icon
              boxShadow: '0px 4px 10px rgba(0, 0, 0, 0.2)', // More visibility
            }}>
              <img
                src={require(`./../../assets/${service.icon}`)}
                alt={`${service.title} Icon`}
                style={{ width: '50px', height: '50px' }}
              />
            </div>

            {/* Title */}
            <h2 style={{ fontSize: '18px', color: '#333', marginTop: '30px' }}>{service.title}</h2>

            <ul style={{ listStyleType: 'none', paddingLeft: '0', textAlign: 'left', marginTop: '15px' }}>
              {service.items.map((item, idx) => (
                <li key={idx} style={{ fontSize: '14px', color: '#666', marginBottom: '8px', position: 'relative', paddingLeft: '15px' }}>
                  <span style={{ position: 'absolute', left: '0', color: '#4CAF50' }}>•</span>{item}
                </li>
              ))}
            </ul>
          </div>
        ))}
      </div>
    </div>
  );
};

const services = [
  {
    icon: '1.png',
    title: 'Environmental Authorisations',
    items: [
      'Environmental Impact Assessments',
      'Water Use Licence Applications (WULA) and Integrated Water and Waste Management Plans (IWWMP)',
      'EMP Development and Performance Assessment',
      'Rehabilitation and Closure Plans',
      'Financial Provision',
    ],
  },
  {
    icon: '2.png',
    title: 'Management System Solutions',
    items: [
      'Integrated EHS Management Systems',
      'Onsite EHS Compliance Officers/ Practitioners',
      'EHS System Audits and Training',
      'Leadership Training',
    ],
  },
  {
    icon: '3.png',
    title: 'EHS Legal Compliance Services',
    items: [
      'EHS Legal Compliance Audits',
      'Due Diligence Audits',
      'Compliance of Legal Registers',
      'Permitting Register',
      'Permitting Compliance Frameworks and Strategies',
      'ESG Compliance Audits',
      'ESG Compliance Frameworks and Strategies',
    ],
  },
];

export default Services;
