import React from 'react';
import HeroSection from '../../components/landingpage/HeroSection.js';
import AboutUs from './../../components/landingpage/AboutUs';
import Services from './../../components/landingpage/Services';
import Portfolio from './../../components/landingpage/Portfolio';
import Testimonials from './../../components/landingpage/Testimonials';
import ContactUs from './../../components/landingpage/ContactUs';
import FAQ from './../../components/landingpage/FAQ';
// import "./../../css/landingPage.css";

const LandingPage = () => {
  const scrollToSection = (id) => {
    const element = document.getElementById(id);
    if (element) {
      element.scrollIntoView({ behavior: 'smooth' });
    }
  };

  return (
    <div>
      <header style={{ 
        position: 'fixed', 
        top: 0, 
        left: 0, 
        right: 0, 
        backgroundColor: '#00183d', 
        zIndex: 1000, 
        padding: '20px 40px', 
        height: '50px', 
        display: 'flex', 
        alignItems: 'center', 
        justifyContent: 'space-between' 
      }}>
        <img src={require('./../../assets/logo.png')} alt="Logo" style={{ height: '60px' }} />
        <nav>
          <ul style={{ display: 'flex', listStyleType: 'none', margin: 0, padding: 0 }}>
            <li style={{ marginRight: '50px' }}>
              <a 
                href="#about-us" 
                onClick={(e) => { e.preventDefault(); scrollToSection('about-us'); }} 
                style={{ 
                  color: 'white', 
                  textDecoration: 'none', 
                  fontWeight: 'bold', 
                  fontSize: '18px', 
                  transition: 'color 0.3s', 
                }}
                onMouseEnter={(e) => e.currentTarget.style.color = '#FFD700'}
                onMouseLeave={(e) => e.currentTarget.style.color = 'white'}
              >
                About Us
              </a>
            </li>
            <li style={{ marginRight: '50px' }}>
              <a 
                href="#services" 
                onClick={(e) => { e.preventDefault(); scrollToSection('services'); }} 
                style={{ 
                  color: 'white', 
                  textDecoration: 'none', 
                  fontWeight: 'bold', 
                  fontSize: '18px', 
                  transition: 'color 0.3s', 
                }}
                onMouseEnter={(e) => e.currentTarget.style.color = '#FFD700'}
                onMouseLeave={(e) => e.currentTarget.style.color = 'white'}
              >
                Services
              </a>
            </li>
            <li>
              <a 
                href="#portfolio" 
                onClick={(e) => { e.preventDefault(); scrollToSection('portfolio'); }} 
                style={{ 
                  color: 'white', 
                  textDecoration: 'none', 
                  fontWeight: 'bold', 
                  fontSize: '18px', 
                  transition: 'color 0.3s', 
                }}
                onMouseEnter={(e) => e.currentTarget.style.color = '#FFD700'}
                onMouseLeave={(e) => e.currentTarget.style.color = 'white'}
              >
                Portfolio
              </a>
            </li>
          </ul>
        </nav>
      </header>

      <div style={{ paddingTop: '120px' }}> {/* Increased padding to prevent content from hiding behind the header */}
        <HeroSection />

        <div id="about-us">
          <AboutUs />
        </div>
        <div id="services">
          <Services />
        </div>
        <div id="portfolio">
          <Portfolio />
        </div>

        <Testimonials />
        <ContactUs />
        <FAQ />
      </div>
    </div>
  );
};

export default LandingPage;
