import React, { useEffect, useState } from 'react';

const HeroSection = () => {
  const [showContactModal, setShowContactModal] = useState(false);

  return (
    <div className="flex flex-col md:flex-row items-center justify-between p-6 md:p-12 bg-white">
      {/* Left side - Text content */}
      <div className="w-full md:w-1/2 mb-8 md:mb-0">
        <p className="text-lg text-gray-800 mb-2">Welcome to</p>
        <h1 className="text-5xl font-bold text-black mb-4">
          Flair Concepts &<br />
          Media (Pty) Ltd
        </h1>
        <p className="text-base text-gray-700 max-w-xl">
          We are a premier communication consultancy providing expert
          media services, content creation, and public relations solutions to
          a diverse portfolio of clients.
        </p>
      </div>

      {/* Right side - Image */}
      <div className="w-full md:w-1/2">
        <img src={require('./../../assets/laptop.png')} alt="Newspaper and laptop" />
      </div>

      {/* Contact Modal logic would go here */}
      {showContactModal && (
        <div className="modal">
          {/* Modal content */}
        </div>
      )}
    </div>
  );
};

export default HeroSection;
