import React from 'react';

const ContactUs = () => {
  return (
    <>
      <section className="contact-us-section">
        <div className="contact-us-content">
          <div className="contact-us-header">
            <h1 style={{ color: '#02f7ee', fontSize: '4rem', marginBottom: '20px' }}>Get in Touch</h1>
            <p style={{ color: 'white', fontSize: '1.5rem', marginBottom: '40px' }}>Contact us for any queries or support.</p>
          </div>
          <div className="contact-us-right">
            <h2 style={{ color: '#02f7ee', fontSize: '2rem', marginBottom: '20px' }}>Contact Information</h2>
            <div className="contact-info">
              <p style={{ color: 'white', fontSize: '1.5rem', marginBottom: '15px' }}>
                Address: 362 Laydes Street, Pretoria
              </p>
              <p style={{ color: 'white', fontSize: '1.5rem', marginBottom: '15px' }}>
                Phone: 078 021 1698
              </p>
              <p style={{ color: 'white', fontSize: '1.5rem', marginBottom: '15px' }}>
                Email: <a href="mailto:blessie999@hmail.com" style={{ color: 'white', textDecoration: 'underline' }}>blessie999@hmail.com</a>
              </p>
            </div>
          </div>
        </div>
      </section>

      <style jsx>{`
        .contact-us-section {
          padding: 100px 20px;
          background-color: #00183d; 
        }

        .contact-us-content {
          max-width: 1200px;
          margin: 0 auto;
          display: flex;
          flex-direction: column;
          justify-content: center;
          align-items: center;
        }

        .contact-us-header {
          text-align: center;
          margin-bottom: 40px;
        }

        .contact-us-right {
          width: 100%;
          margin: 10px 0;
          padding: 30px;
          background-color: #01224a;
          border-radius: 10px;
          box-shadow: 0 4px 20px rgba(0, 0, 0, 0.2);
          min-height: 400px;
          display: flex;
          flex-direction: column;
          justify-content: space-between;
        }

        .contact-info {
          margin-bottom: 40px;
        }

        .contact-us-right:hover {
          box-shadow: 0 6px 30px rgba(0, 0, 0, 0.3);
        }
      `}</style>
    </>
  );
};

export default ContactUs;
