import React from 'react';

const ContactUs = () => {
  return (
    <>
      <section className="contact-us-section">
        <div className="contact-us-content">
          <div className="contact-us-left">
            <h2 style={{ color: '#02f7ee', fontSize: '3rem', marginBottom: '20px' }}>Contact Information</h2>
            <p style={{ color: 'white', fontSize: '1.5rem', marginBottom: '15px' }}>
              Mabunda Tech
              <br />
              123 Main St, Anytown, USA
              <br />
              <a href="https://www.google.com/maps/place/Anytown,+USA" target="_blank" rel="noopener noreferrer" style={{ color: 'white', textDecoration: 'underline' }}>
                View on Google Maps
              </a>
            </p>
            <h3 style={{ color: '#02f7ee', fontSize: '1.5rem', marginBottom: '15px' }}>Phone & Email</h3>
            <p style={{ color: 'white', fontSize: '1.5rem' }}>
              Phone: 555-555-5555
              <br />
              Email: <a href="mailto:info@mabundatech.com" style={{ color: 'white', textDecoration: 'underline' }}>info@mabundatech.com</a>
            </p>
          </div>
          <div className="contact-us-right">
            <h3 style={{ color: '#02f7ee', fontSize: '1.5rem', marginBottom: '15px' }}>Social Media Links</h3>
            <p style={{ color: 'white' }}>
              <a href="https://www.linkedin.com/company/mabundatech" target="_blank" rel="noopener noreferrer" style={{ color: 'white', textDecoration: 'underline', fontSize: '1.5rem' }}>LinkedIn</a>
              <br />
              <a href="https://twitter.com/mabundatech" target="_blank" rel="noopener noreferrer" style={{ color: 'white', textDecoration: 'underline', fontSize: '1.5rem' }}>Twitter</a>
              <br />
              <a href="https://github.com/mabundatech" target="_blank" rel="noopener noreferrer" style={{ color: 'white', textDecoration: 'underline', fontSize: '1.5rem' }}>GitHub</a>
            </p>
          </div>
        </div>
      </section>

      <style jsx>{`
        .contact-us-section {
          padding: 100px 20px;
          background-color: #00183d;
        }

        .contact-us-content {
          max-width: 800px;
          margin: 0 auto;
          display: flex;
          flex-direction: column;
          justify-content: center;
          align-items: center;
          gap: 20px;
        }

        .contact-us-left,
        .contact-us-right {
          width: 100%;
          padding: 30px;
          background-color: #01224a;
          border-radius: 10px;
          box-shadow: 0 4px 20px rgba(0, 0, 0, 0.2);
        }

        @media (min-width: 768px) {
          .contact-us-content {
            flex-direction: row;
            align-items: stretch;
          }
          .contact-us-left,
          .contact-us-right {
            width: calc(50% - 10px);
          }
        }
      `}</style>
    </>
  );
};

export default ContactUs;
