import React, { useEffect } from 'react';

const HeroSection = () => {
  useEffect(() => {
    const heroText = document.querySelector('.hero-text');
    const heroButton = document.querySelector('.hero-button-container');
    const heroImage = document.querySelector('.hero-image');

    setTimeout(() => {
      heroText.classList.add('fade-in');
      heroButton.classList.add('slide-up');
      heroImage.classList.add('zoom-in');
    }, 100);
  }, []);

  return (
    <>
      <section className="hero-section">
        <div className="hero-content">
          <div className="hero-text">
            <div className="hero-title">
              <h1>Empowering Innovation, <span>One Line of Code at a Time.</span></h1>
            </div>
            <p>Transforming ideas into innovative software solutions, empowering businesses to thrive in a digital world.</p>
            <div className="hero-button-container">
              <button>Let's Build Something</button>
            </div>
          </div>
          <div className="hero-image">
            <img src={require('./../../assets/code.jpg')} alt="Code" />
          </div>
        </div>
      </section>

      <style jsx>{`
        .hero-section {
          background-color: #00183d;
          padding: 80px 20px;
          color: white;
          display: flex;
          justify-content: center;
          align-items: center;
          height: 50vh;
          text-align: center;
        }

        .hero-content {
          display: flex;
          justify-content: space-between;
          align-items: center;
          max-width: 1200px;
          width: 100%;
          padding: 0 20px;
          flex-wrap: wrap; /* Enables wrapping on smaller screens */
        }

        .hero-text {
          flex: 1;
          text-align: center;
          min-width: 280px; /* Prevents shrinking too much */
        }

        .hero-text h1 {
          font-size: 2rem;
        }

        .hero-text h1 span {
          color: #02f7ee;
        }

        .hero-text p {
          font-size: 1rem;
          margin-bottom: 20px;
        }

        .hero-button-container {
          margin-top: 20px;
        }

        .hero-button-container button {
          background-color: #02f7ee;
          color: #00183d;
          padding: 12px 25px;
          border: none;
          font-size: 1rem;
          cursor: pointer;
          border-radius: 5px;
        }

        .hero-image {
          flex: 1;
          min-width: 280px; /* Ensures image doesn't shrink too much */
          text-align: center;
          max-width: 400px;
          opacity: 0;
          transform: scale(0.9);
          animation: zoomIn 2s forwards;
        }

        .hero-image img {
          width: 100%;
          max-width: 300px;
          border-radius: 10px;
          box-shadow: 0 4px 10px rgba(0, 0, 0, 0.2);
        }

        @media (max-width: 768px) {
          .hero-content {
            flex-direction: column;
            text-align: center;
          }

          .hero-text, .hero-image {
            flex-basis: 100%;
            min-width: 100%;
          }

          .hero-image img {
            max-width: 250px;
          }

          .hero-text h1 {
            font-size: 1.6rem;
          }

          .hero-text p {
            font-size: 0.9rem;
          }

          .hero-button-container button {
            font-size: 0.9rem;
            padding: 10px 20px;
          }
        }

        @keyframes zoomIn {
          to {
            opacity: 1;
            transform: scale(1);
          }
        }
      `}</style>
    </>
  );
};

export default HeroSection;
