import React, { useEffect, useState } from 'react';

const HeroSection = () => {
  useEffect(() => {
    const heroText = document.querySelector('.hero-text');
    const heroImage = document.querySelector('.hero-image');

    setTimeout(() => {
      heroText.classList.add('fade-in');
      heroImage.classList.add('zoom-in');
    }, 100);
  }, []);

  return (
    <>
      <section className="hero-section">
        <div className="hero-content">
          <div className="hero-text">
            <p className="welcome-text">Welcome to</p>
            <h1 className="company-name">Flair Concepts &</h1>
            <h1 className="company-name">Media (Pty) Ltd</h1>
            <p className="company-description">
              We are a premier communication consultancy providing expert media services, content 
              creation, and public relations solutions to a diverse portfolio of clients.
            </p>
          </div>
          <div className="hero-image">
            <img src={require('./../../assets/laptop.png')} alt="Newspaper and laptop" />
          </div>
        </div>
      </section>

      <style jsx>{`
        body {
          overflow-x: hidden;
          margin: 0;
          padding: 0;
        }

        .hero-section {
          background-color: white;
          padding: 80px 0;
          color: black;
          display: flex;
          justify-content: center;
          align-items: center;
          min-height: 100vh;
          width: 100%;
          overflow: hidden;
        }

        .hero-content {
          display: flex;
          align-items: center;
          justify-content: space-between;
          max-width: 1200px;
          width: 100%;
          padding: 0 20px;
        }

        .hero-text {
          flex: 1;
          text-align: left;
        }

        .welcome-text {
          font-size: 1.5rem;
          margin-bottom: 10px;
          font-weight: normal;
        }

        .company-name {
          font-size: 4rem;
          font-weight: bold;
          margin: 0;
        }

        .company-description {
          font-size: 1.1rem;
          line-height: 1.5;
          margin: 20px 0;
        }

        .hero-image {
          flex: 1;
          position: relative;
          overflow: hidden;
          width: 100%;
          height: 100%;
          box-shadow: 0 0 20px rgba(0, 0, 0, 0.2);
        }

        .hero-image img {
          width: 100%;
          height: 100%;
          object-fit: cover; /* Makes the image cover the space */
        }

        .hero-text h1, .hero-text p {
          opacity: 0;
          animation: fadeInText 1.5s forwards;
        }

        @keyframes fadeInText {
          to { opacity: 1; }
        }

        @keyframes zoomIn {
          to { opacity: 1; transform: scale(1); }
        }

        @media (max-width: 768px) {
          .hero-content {
            flex-direction: column;
            text-align: center;
          }

          .hero-text {
            flex: 1;
            margin-bottom: 20px;
            text-align: center;
          }

          .hero-image {
            flex: 1;
            height: 300px; /* Adjust the height for smaller screens */
          }

          .company-name {
            font-size: 2.2rem;
          }

          .company-description {
            font-size: 1rem;
          }
        }
      `}</style>
    </>
  );
};

export default HeroSection;
