import React from 'react';

const ContactUs = () => {
  return (
    <>
      <section className="contact-us-section">
        <div className="contact-us-content">
          <div className="contact-us-left">
            <form>
              <input
                type="text"
                placeholder="Name"
                style={inputStyle}
              />
              <input
                type="email"
                placeholder="Email"
                style={inputStyle}
              />
              <textarea
                placeholder="Message"
                style={{ ...inputStyle, height: '100px' }}
              />
              <button
                type="submit"
                style={{
                  backgroundColor: '#01224a',
                  color: 'white',
                  fontSize: '1.5rem',
                  border: 'none',
                  padding: '12px 24px',
                  borderRadius: '10px',
                  cursor: 'pointer',
                  transition: 'background-color 0.3s',
                }}
                onMouseEnter={(e) => (e.currentTarget.style.backgroundColor = '#0A2E61')}
                onMouseLeave={(e) => (e.currentTarget.style.backgroundColor = '#01224a')}
              >
                Send Message
              </button>
            </form>
          </div>
          <div className="contact-us-right">
            <h2 style={{ color: '#02f7ee', fontSize: '3rem', marginBottom: '20px' }}>Contact Information</h2>
            <p style={{ color: 'white', fontSize: '1.5rem', marginBottom: '15px' }}>
              Mabunda Tech
              <br />
              123 Main St, Anytown, USA
              <br />
              <a href="https://www.google.com/maps/place/Anytown,+USA" target="_blank" rel="noopener noreferrer" style={{ color: 'white', textDecoration: 'underline' }}>
                View on Google Maps
              </a>
            </p>
            <h2 style={{ color: '#02f7ee', fontSize: '1.5rem', marginBottom: '15px' }}>Phone & Email</h2>
            <p style={{ color: 'white', fontSize: '1.5rem', marginBottom: '15px' }}>
              Phone: 555-555-5555
              <br />
              Email: <a href="mailto:info@mabundatech.com" style={{ color: 'white', textDecoration: 'underline' }}>info@mabundatech.com</a>
            </p>
          </div>
        </div>
      </section>

      <style jsx>{`
        .contact-us-section {
          padding: 100px 20px;
          background-color: #00183d; 
        }

        .contact-us-content {
          max-width: 1200px;
          margin: 0 auto;
          display: flex;
          flex-direction: column;
          justify-content: center;
          align-items: center;
        }

        .contact-us-left,
        .contact-us-right {
          width: 100%;
          margin: 20px 0;
          padding: 30px;
          background-color: #01224a;
          border-radius: 10px;
          box-shadow: 0 4px 20px rgba(0, 0, 0, 0.2);
          min-height: 400px;
          display: flex;
          flex-direction: column;
          justify-content: space-between;
        }

        @media (min-width: 768px) {
          .contact-us-content {
            flex-direction: row;
            align-items: stretch;
          }

          .contact-us-left {
            margin-right: 20px;
          }

          .contact-us-right {
            margin-left: 20px;
          }

          .contact-us-left,
          .contact-us-right {
            width: calc(50% - 20px);
          }
        }

        .contact-us-left:hover,
        .contact-us-right:hover {
          box-shadow: 0 6px 30px rgba(0, 0, 0, 0.3);
        }
      `}</style>
    </>
  );
};

const inputStyle = {
  padding: '12px',
  margin: '10px 0',
  width: '100%',
  borderRadius: '5px',
  border: '1px solid #ccc',
  fontSize: '1rem',
  backgroundColor: '#f9f9f9',
};

export default ContactUs;
