import React from 'react';

const AboutUs = () => {
  return (
    <>
      {/* <section className="hero-section">
        <h1>About Us</h1>
        <p className="section-subtitle">Company Overview</p>
      </section> */}

      <section className="about-us-section">
        <div className="about-us-content">
          <div className="about-us-text">
          <h1>About Us</h1>
          <p className="section-subtitle">Company Overview</p>
            <p>
              CodeCraft Co. was founded in 2010 with a vision to empower businesses through innovative software solutions. Our mission is to deliver high-quality, cutting-edge technology solutions that meet the evolving needs of our clients.
            </p>
            <p>
              Over the years, we have built a team of experienced professionals who share our passion for innovation and excellence. We take pride in our ability to understand our clients' unique challenges and develop tailored solutions that drive business success.
            </p>
          </div>
          <div className="about-us-stats">
            <div className="stat">
              <h2>10+</h2>
              <p>Years of Experience</p>
            </div>
            <div className="stat">
              <h2>50+</h2>
              <p>Successful Projects</p>
            </div>
            <div className="stat">
              <h2>20+</h2>
              <p>Team Members</p>
            </div>
          </div>
        </div>
      </section>

      <style jsx>{`
        // .hero-section {
        //   background-color: #00183d; 
        //   padding: 100px 0;
        //   color: white;
        //   display: flex;
        //   justify-content: center;
        //   align-items: center;
        //   height: 50vh;
        // }

        .about-us-section {
          padding: 100px 0;
         background-color: #00183d; 
         color: white;
        }

        .about-us-content {
          max-width: 1200px;
          margin: 0 auto;
          padding: 0 20px;
        }

        .about-us-text {
          margin-bottom: 40px;
        }

        .about-us-text p {
          font-size: 1.2rem;
          margin-bottom: 20px;
        }

        .about-us-stats {
          display: flex;
          justify-content: space-between;
          align-items: center;
        }

        .stat {
          text-align: center;
          margin: 0 20px;
        }

        .stat h2 {
          font-size: 2rem;
          margin-bottom: 10px;
        }

        .stat p {
          font-size: 1.2rem;
        }
      `}</style>
    </>
  );
};

export default AboutUs;
