import React from 'react';
import { motion } from 'framer-motion';

const SustainabilityHero = () => {
  return (
    <motion.div 
      initial={{ opacity: 0, y: 30 }} 
      animate={{ opacity: 1, y: 0 }} 
      transition={{ duration: 1 }}
      className="flex flex-row max-w-5xl mx-auto p-6 gap-10 items-center bg-cover bg-center rounded-lg"
      style={{ backgroundImage: "url(" + require("./../../assets/global.jpg") + ")" }}
    >
      <motion.div 
        initial={{ opacity: 0, x: -30 }}
        animate={{ opacity: 1, x: 0 }}
        transition={{ duration: 1.2 }}
        className="flex-1 bg-white bg-opacity-80 p-6 rounded-lg text-center"
      >
        <div className="mb-6">
          <img src={require("./../../assets/logo.png")} alt="Logo" className="h-20 w-20 mx-auto" />
        </div>
        <p className="text-green-700 font-extrabold mb-2">We Are Madzenga Consulting</p>
        <h1 className="text-4xl font-bold text-gray-900 leading-tight">Building a Greener</h1>
        <h1 className="text-4xl font-bold text-gray-900 leading-tight">Economy Through</h1>
        <h1 className="text-4xl font-bold leading-tight">
          <span className="text-green-600">Sustainable </span>
          <span className="text-orange-500">Solutions</span>
        </h1>
        <p className="text-gray-600 my-6 max-w-lg mx-auto leading-relaxed">
          Ensuring compliance, innovation, and environmental stewardship for a sustainable future.
        </p>
        <motion.button 
          whileHover={{ scale: 1.1, backgroundColor: "#f97316" }}
          whileTap={{ scale: 0.95 }}
          className="bg-orange-400 text-white font-bold py-3 px-6 rounded-lg transition-all"
        >
          Quality Assurance
        </motion.button>
      </motion.div>
    </motion.div>
  );
};

export default SustainabilityHero;
