import React from 'react';

const Services = () => {
  return (
    <div style={{ fontFamily: 'Arial, sans-serif', padding: '20px', maxWidth: '1200px', margin: '0 auto' }}>
      <h1 style={{ fontSize: '24px', color: '#333', marginBottom: '40px', textAlign: 'center' }}>Our Services</h1>

      <div style={{
        display: 'flex',
        flexWrap: 'wrap',
        justifyContent: 'center',
        gap: '10px',
        marginBottom: '40px',
      }}>
        {services.map((service, index) => (
          <div key={index} style={{
            flex: '1 0 100%', // Full width on mobile
            maxWidth: '100%', // Ensure it fits within the container
            marginBottom: '20px', // Gap between images
            boxSizing: 'border-box',
          }}>
            <img
              src={require(`./../../assets/${service.icon}`)}
              alt={`Service ${index + 1}`}
              style={{
                width: '100%',
                height: 'auto',
                objectFit: 'contain',
                display: 'block',
              }}
            />
          </div>
        ))}
      </div>
    </div>
  );
};

const services = [
  { icon: '1.png' },
  { icon: '2.png' },
  { icon: '3.png' },
];

export default Services;
