import React, { useState, useCallback, useEffect } from "react";
import { useNavigate } from "react-router-dom";

const AboutUs = () => {
  const [headerColor, setHeaderColor] = useState("green");
  const [sidebarOpen, setSidebarOpen] = useState(false);
  const [isSqueezing, setIsSqueezing] = useState(false);
  const navigate = useNavigate();

  const handleScroll = useCallback(() => {
    requestAnimationFrame(() => {
      setHeaderColor(window.scrollY > 50 ? "green" : "green");
    });
  }, []);

  useEffect(() => {
    document.body.style.overflowX = "hidden";
    document.documentElement.style.overflowX = "hidden";

    window.addEventListener("scroll", handleScroll);
    return () => {
      window.removeEventListener("scroll", handleScroll);
      document.body.style.overflowX = "";
      document.documentElement.style.overflowX = "";
    };
  }, [handleScroll]);

  const toggleSidebar = () => {
    if (sidebarOpen) {
      setSidebarOpen(false);
      setIsSqueezing(false);
    } else {
      setIsSqueezing(true);
      setTimeout(() => {
        setSidebarOpen(true);
      }, 1000); // Wait for the content to disappear before opening the sidebar
    }
  };

  useEffect(() => {
    if (sidebarOpen || isSqueezing) {
      document.body.style.overflow = "hidden";
    } else {
      document.body.style.overflow = "";
    }
  }, [sidebarOpen, isSqueezing]);

  const scrollToSection = (id) => {
    setTimeout(() => {
      const element = document.getElementById(id);
      if (element) {
        element.scrollIntoView({ behavior: "smooth", block: "start" });
        setSidebarOpen(false);
        setIsSqueezing(false);
      }
    }, 100);
  };

  // New function to handle navigation to About page
  const navigateToAbout = (e) => {
    e.preventDefault();
    navigate("/About");
    // Close sidebar if it's open
    setSidebarOpen(false);
    setIsSqueezing(false);
  };

  // Handler for the Get Started button
  const handleGetStarted = (e) => {
    e.preventDefault();
    // You can either navigate to a page:
    // navigate("/get-started");
    // Or scroll to contact section:
    scrollToSection("contact-us");
  };

  return (
    <div className={`page-wrapper ${isSqueezing ? "squeezing" : ""} ${sidebarOpen ? "sidebar-active" : ""}`}>
      {/* Header Section */}
      <header
        className={`main-header ${isSqueezing ? "header-squeezing" : ""} ${sidebarOpen ? "header-shifted" : ""}`}
        style={{
          position: "fixed",
          top: 0,
          left: 0,
          right: 0,
          backgroundColor: headerColor,
          zIndex: 1000,
          padding: "10px 15px",
          height: "80px",
          display: "flex",
          alignItems: "center",
          justifyContent: "space-between",
          width: "100%",
        }}
      >
        <div style={{ fontSize: "20px", fontWeight: "bold", color: "white" }}>
          Madzenga Consulting
        </div>

        <nav className="desktop-nav">
          <ul className="nav-list">
            <li>
              <a
                href="/About"
                onClick={navigateToAbout}
                className="nav-link"
                style={{ color: "white" }}
              >
                About Us
              </a>
            </li>
            <li>
              <a
                href="#services"
                onClick={(e) => {
                  e.preventDefault();
                  scrollToSection("services");
                }}
                className="nav-link"
                style={{ color: "white" }}
              >
                Services
              </a>
            </li>
            <li>
              <a
                href="#projects"
                onClick={(e) => {
                  e.preventDefault();
                  scrollToSection("proofreading");
                }}
                className="nav-link"
                style={{ color: "white" }}
              >
                Projects
              </a>
            </li>
            <li>
              <a
                href="#quality"
                onClick={(e) => {
                  e.preventDefault();
                  scrollToSection("proofreading");
                }}
                className="nav-link"
                style={{ color: "white" }}
              >
                Quality Assurance
              </a>
            </li>
            <li>
              <a
                href="#contact-us"
                onClick={handleGetStarted}
                className="nav-link-button"
                style={{ color: "white" }}
              >
                Get Started
              </a>
            </li>
          </ul>
        </nav>

        <div className="mobile-menu-icon" onClick={toggleSidebar}>
          <div className={`hamburger ${sidebarOpen ? "open" : ""}`}>
            <span></span>
            <span></span>
            <span></span>
          </div>
        </div>
      </header>

      {/* Mobile Sidebar */}
      <div className={`mobile-sidebar ${sidebarOpen ? "open" : ""}`}>
        <div style={{ display: "flex", justifyContent: "center", padding: "0px", marginTop: "-50px" }}>
          <img
            src={require("./../../assets/logo.png")}
            alt="Logo"
            style={{ height: "60px", width: "60px" }}
          />
        </div>
        <div className="sidebar-close" onClick={toggleSidebar}>
          âœ•
        </div>
        <ul className="sidebar-nav-list" style={{ marginTop: "10px" }}>
          <li>
            <a href="/About" onClick={navigateToAbout} className="sidebar-nav-link">
              About Us
            </a>
          </li>
          <li>
            <a href="#services" onClick={(e) => { e.preventDefault(); scrollToSection("services"); }} className="sidebar-nav-link">
              Services
            </a>
          </li>
          <li>
            <a href="#projects" onClick={(e) => { e.preventDefault(); scrollToSection("proofreading"); }} className="sidebar-nav-link">
              Projects
            </a>
          </li>
          <li>
            <a href="#quality" onClick={(e) => { e.preventDefault(); scrollToSection("proofreading"); }} className="sidebar-nav-link">
              Quality Assurance
            </a>
          </li>
          <li>
            <a 
              href="#contact-us" 
              onClick={(e) => { e.preventDefault(); scrollToSection("contact-us"); }} 
              className="sidebar-nav-button"
            >
              Get Started
            </a>
          </li>
        </ul>
      </div>

      {/* Overlay */}
      {(sidebarOpen || isSqueezing) && (
        <div className="sidebar-overlay" onClick={toggleSidebar}></div>
      )}

      {/* About Us Content */}
      <div 
        style={{ 
          minHeight: "100vh", 
          backgroundColor: "#f3f4f6", 
          display: "flex", 
          justifyContent: "center", 
          alignItems: "center", 
          padding: "20px",
          paddingTop: "100px" // Added padding to account for the fixed header
        }}
      >
        <div 
          style={{ 
            maxWidth: "1200px", 
            width: "100%", 
            display: "grid", 
            gridTemplateColumns: "repeat(auto-fit, minmax(300px, 1fr))", 
            gap: "20px" 
          }}
        >
          <div 
            style={{ 
              backgroundColor: "white", 
              padding: "20px", 
              borderRadius: "15px", 
              boxShadow: "0 4px 8px rgba(0, 0, 0, 0.1)" 
            }}
          >
            <h2 style={{ color: "#2563eb", fontSize: "24px", fontWeight: "bold", marginBottom: "10px" }}>
              Our Mission
            </h2>
            <p style={{ color: "#4b5563" }}>
              Our mission is to empower businesses with innovative solutions, ensuring
              excellence and long-term success.
            </p>
          </div>

          <div 
            style={{ 
              backgroundColor: "white", 
