import React, { useEffect, useState, useCallback } from "react";
import HeroSection from "../../components/landingpage/HeroSection.js";
import AboutUs from "../../components/landingpage/AboutUs";
import Services from "../../components/landingpage/Services";
import Portfolio from "../../components/landingpage/Portfolio";
import Testimonials from "../../components/landingpage/Testimonials";
import ContactUs from "../../components/landingpage/ContactUs";
import FAQ from "../../components/landingpage/FAQ";

const LandingPage = () => {
  const [headerColor, setHeaderColor] = useState("#89298d");
  const [sidebarOpen, setSidebarOpen] = useState(false);

  const handleScroll = useCallback(() => {
    requestAnimationFrame(() => {
      setHeaderColor(window.scrollY > 50 ? "#561959" : "#89298d");
    });
  }, []);

  useEffect(() => {
    document.body.style.overflowX = "hidden";
    document.documentElement.style.overflowX = "hidden";

    window.addEventListener("scroll", handleScroll);
    return () => {
      window.removeEventListener("scroll", handleScroll);
      document.body.style.overflowX = "";
      document.documentElement.style.overflowX = "";
    };
  }, [handleScroll]);

  const scrollToSection = (id) => {
    setTimeout(() => {
      const element = document.getElementById(id);
      if (element) {
        element.scrollIntoView({ behavior: "smooth", block: "start" });
        // Close sidebar after clicking a link on mobile
        setSidebarOpen(false);
      }
    }, 100);
  };

  return (
    <div style={{ width: "100vw", overflowX: "hidden" }}>
      {/* Header Section */}
      <header
        style={{
          position: "fixed",
          top: 0,
          left: 0,
          right: 0,
          backgroundColor: headerColor,
          zIndex: 1000,
          padding: "10px 15px",
          height: "110px",
          display: "flex",
          alignItems: "center",
          justifyContent: "space-between",
          transition: "background-color 0.3s ease",
          width: "100%",
        }}
      >
        {/* Updated logo container with corrected dimensions */}
        <div className="logo-container" style={{
          width: '100px',  // Reduced width to match image proportions
          height: '60px',
          borderRadius: '25px', // Adjusted to maintain proportions
          backgroundColor: 'white',
          display: 'flex',
          justifyContent: 'center',
          alignItems: 'center',
          padding: '5px',
          border: '2px solid white',
          boxShadow: '0 0 8px rgba(138, 43, 226, 0.4)',
          overflow: 'hidden',
        }}>
          <img
            src={require("./../../assets/logo.png")}
            alt="Logo"
            style={{ height: "45px", width: "45px" }}
          />
        </div>

        {/* Desktop Navigation - only visible on desktop */}
        <nav className="desktop-nav">
          <ul className="nav-list">
            <li>
              <a href="#about-us" onClick={(e) => { e.preventDefault(); scrollToSection("about-us"); }} className="nav-link">
                About Us
              </a>
            </li>
            <li>
              <a href="#services" onClick={(e) => { e.preventDefault(); scrollToSection("services"); }} className="nav-link">
                Our Services
              </a>
            </li>
            <li>
            <a href="/proofreading-services" onClick={handleNavigation} className="nav-link">
              Proofreading Services
            </a>
          </li>
            <li>
              <a href="https://flairmagazineza.wordpress.com" className="nav-link-button" target="_blank" rel="noopener noreferrer">
                The Flair Magazine ZA
              </a>
            </li>
          </ul>
        </nav>

        {/* Mobile menu hamburger icon - only visible on mobile */}
        <div className="mobile-menu-icon" onClick={() => setSidebarOpen(!sidebarOpen)}>
          <div className={`hamburger ${sidebarOpen ? 'open' : ''}`}>
            <span></span>
            <span></span>
            <span></span>
          </div>
        </div>
      </header>

      {/* Mobile Sidebar - only visible on mobile when toggled */}
      <div className={`mobile-sidebar ${sidebarOpen ? 'open' : ''}`}>
        <div className="sidebar-close" onClick={() => setSidebarOpen(false)}>
          ✕
        </div>
        <ul className="sidebar-nav-list">
          <li>
            <a href="#about-us" onClick={(e) => { e.preventDefault(); scrollToSection("about-us"); }} className="sidebar-nav-link">
              About Us
            </a>
          </li>
          <li>
            <a href="#services" onClick={(e) => { e.preventDefault(); scrollToSection("services"); }} className="sidebar-nav-link">
              Our Services
            </a>
          </li>
          <li>
          <a href="/proofreading-services" onClick={handleNavigation} className="nav-link">
            Proofreading Services
          </a>
        </li>
          <li>
            <a href="https://flairmagazineza.wordpress.com" className="sidebar-nav-button" target="_blank" rel="noopener noreferrer">
              The Flair Magazine ZA
            </a>
          </li>
        </ul>
      </div>

      {/* Overlay for when sidebar is open */}
      {sidebarOpen && <div className="sidebar-overlay" onClick={() => setSidebarOpen(false)}></div>}

      {/* Page Content */}
      <div style={{ paddingTop: "50px", width: "100vw", overflowX: "hidden" }}>
        <HeroSection />
        <div id="about-us"><AboutUs /></div>
        <Services />
        <div id="services"><Testimonials /></div>
        <div id="proofreading"><Portfolio /></div>
        {/* <Services /> */}
        <Testimonials />
        <ContactUs />
        {/* <FAQ /> */}
      </div>

      {/* Global Styles */}
      <style>
        {`
          * {
            margin: 0;
            padding: 0;
            box-sizing: border-box;
            overflow-x: hidden;
          }

          body, html {
            width: 100%;
            overflow-x: hidden;
          }

          .nav-list {
            display: flex;
            list-style-type: none;
            margin: 0;
            padding: 0;
            gap: 60px;
            align-items: center;
          }

          .nav-link {
            color: white;
            text-decoration: none;
            font-weight: normal;
            font-size: 18px;
            transition: color 0.3s;
          }

          .nav-link:hover {
            color: #FFD700;
          }

          .nav-link-button {
            color: #000000; /* Text color */
            background-color: white; /* Background color */
            text-decoration: none; /* Remove underline */
            font-weight: bold; /* Bold text */
            font-size: 16px; /* Text size */
            padding: 12px 20px; /* Adjust padding for better spacing */
            border-radius: 8px; /* Slightly larger border radius */
            box-shadow: 0 4px 6px rgba(0, 0, 0, 0.1); /* Add shadow for depth */
            display: inline-block; /* Ensure it behaves like a button */
            text-align: center; /* Align text */
            transition: all 0.3s; /* Smooth transition on hover */
          }

          .nav-link-button:hover {
            background-color: #8A2BE2; /* Change background on hover */
            color: white; /* Change text color on hover */
            cursor: pointer; /* Show pointer on hover */
            box-shadow: 0 6px 10px rgba(0, 0, 0, 0.2); /* Enhance shadow on hover */
          }

          /* Mobile sidebar styles */
          .mobile-menu-icon {
            display: none;
            cursor: pointer;
          }

          .hamburger {
            display: flex;
            flex-direction: column;
            justify-content: space-between;
            width: 30px;
            height: 20px;
          }

          .hamburger span {
            display: block;
            height: 3px;
            width: 100%;
            background-color: white;
            border-radius: 3px;
            transition: all 0.3s ease;
          }

          .hamburger.open span:nth-child(1) {
            transform: translateY(8px) rotate(45deg);
          }

