import React from 'react';

const AboutUs = () => {
  return (
    <>
      <section className="about-us-section">
        <div className="about-us-content">
          <div className="about-us-text">
            <h1>About Us</h1>
            <p className="section-subtitle">Company Overview</p>
            <p>
              Mabunda Tech was founded in 2025 with a vision to empower businesses and individuals through innovative technology solutions. Our mission is to deliver high-quality, cutting-edge technology solutions that meet the evolving needs of our clients.
            </p>
            <p>
              As a startup, we are driven by a passion for innovation and excellence. We are committed to building a team of experienced professionals who share our vision and values. Our goal is to make a positive impact in the lives of our clients and the community we serve.
            </p>
          </div>
          {/* <div className="about-us-stats">
            <div className="stat">
              <h2>2025</h2>
              <p>Year Founded</p>
            </div>
            <div className="stat">
              <h2>1+</h2>
              <p>Year of Experience</p>
            </div>
            <div className="stat">
              <h2>10+</h2>
              <p>Team Members and Growing</p>
            </div>
          </div> */}
        </div>
      </section>
      <style jsx>{`
        .about-us-section {
          padding: 80px 20px;
          background-color: #00183d;
          color: white;
          display: flex;
          justify-content: center;
          align-items: center;
          animation: fadeIn 1.5s ease-out;
        }

        .about-us-content {
          max-width: 1200px;
          margin: 0 auto;
          text-align: center;
        }

        .about-us-text {
          margin-bottom: 40px;
          font-size: 1.2rem;
          color: #02f7ee;
          animation: fadeIn 1.5s ease-out;
        }

        .about-us-text h1 {
          font-size: 2.5rem;
          color: #5cb6f9;
          margin-bottom: 10px;
          animation: fadeInUp 1.5s ease-out;
        }

        .about-us-text p {
          font-size: 1.3rem;
          margin-bottom: 20px;
          text-align: center;
          opacity: 0;
          color: white; /* Change this line */
          animation: fadeInUp 1.8s ease-out forwards;
        }

        .about-us-stats {
          display: flex;
          justify-content: space-around;
          align-items: center;
          flex-wrap: wrap;
          gap: 20px;
          animation: fadeInUp 2s ease-out forwards;
        }

        .stat {
          text-align: center;
          margin: 10px;
          flex: 1 1 100px;
        }

        .stat h2 {
          font-size: 2rem;
          margin-bottom: 10px;
          color: #f4f4f9;
        }

        .stat p {
          font-size: 1.2rem;
        }

        @media (max-width: 768px) {
          .about-us-section {
            padding: 60px 20px;
          }

          .about-us-text h1 {
            font-size: 2rem;
          }

          .about-us-text p {
            font-size: 1.1rem;
          }

          .about-us-stats {
            flex-direction: column;
          }

          .stat {
            margin-bottom: 15px;
          }
        }

        @keyframes fadeIn {
          from {
            opacity: 0;
          }
          to {
            opacity: 1;
          }
        }

        @keyframes fadeInUp {
          from {
            opacity: 0;
            transform: translateY(20px);
          }
          to {
            opacity: 1;
            transform: translateY(0);
          }
        }
      `}</style>

    </>
  );
};

export default AboutUs;
