import React, { useEffect, useState } from 'react';

const HeroSection = () => {
  const [showContactModal, setShowContactModal] = useState(false);

  useEffect(() => {
    const heroText = document.querySelector('.hero-text');
    const heroButton = document.querySelector('.hero-button-container');
    const heroImage = document.querySelector('.hero-image');

    setTimeout(() => {
      heroText.classList.add('fade-in');
      heroButton.classList.add('slide-up');
      heroImage.classList.add('zoom-in');
    }, 100);
  }, []);

  const handleContactClick = () => {
    setShowContactModal(true);
  };

  const closeModal = () => {
    setShowContactModal(false);
  };

  return (
    <>
      <section className="hero-section">
        <div className="hero-content">
          <div className="hero-text">
            <div className="hero-title">
              <h1 style={{ display: 'inline', marginRight: '5px' }}>Welcome to</h1>
              <h1 style={{ display: 'block', color: '#5cb6f9' }}>Flair Concepts & Media (Pty) Ltd</h1>
            </div>
            <p>We are a premier communication consultancy providing expert media services, content creation, and public relations solutions to a diverse portfolio of clients.</p>
            <div className="hero-button-container">
              <button onClick={handleContactClick}>Let's Connect</button>
            </div>
          </div>
          <div className="hero-image">
            <img src={require('./../../assets/code.jpg')} alt="Flair Concepts & Media" />
          </div>
        </div>
      </section>

      {showContactModal && (
        <div className="modal-overlay">
          <div className="modal">
            <h3>Contact us via:</h3>
            <div className="modal-buttons">
            <a className="modal-button" href="https://wa.me/27780211698" target="_blank" rel="noopener noreferrer">WhatsApp</a>

            <a className="modal-button" href="mailto:blessie999@gmail.com">Email</a>
            </div>
            <button className="close-modal" onClick={closeModal}>Close</button>
          </div>
        </div>
      )}

