import React, { useEffect } from 'react';
import { useNavigate } from 'react-router-dom';  // Change from useHistory to useNavigate

const SplashPage = () => {
  const navigate = useNavigate();  // Use useNavigate hook for navigation

  useEffect(() => {
    // Navigate to the signup page after 5 seconds
    const timer = setTimeout(() => {
      navigate('/LandingPage');  // Use navigate() instead of history.push()
    }, 3000);

    // Cleanup the timer on component unmount
    return () => clearTimeout(timer);
  }, [navigate]);

  const splashContainerStyle = {
    backgroundColor: '#00183d',  // Apply the background color here
    display: 'flex',
    justifyContent: 'center',
    alignItems: 'center',
    height: '100vh',
  };

  const logosStyle = {
    width: '200px',  // Adjust size as needed
    height: 'auto',
  };

  return (
    <div style={splashContainerStyle}>
      <img src={require('./../../assets/logo.png')} alt="logos" style={logosStyle} />
    </div>
  );
};

export default SplashPage;
