import React from 'react';
import { mdiAccountGroup, mdiMusic, mdiOfficeBuilding, mdiPencil } from '@mdi/js';
import Icon from '@mdi/react';

const Services = () => {
  return (
    <div style={styles.container}>
      <h1 style={styles.title}>Public Relations Services</h1>
      <p style={styles.description}>
        We offer PR services for NPOs, community developers, corporate clients, artists, producers, DJs, and other creatives.
      </p>

      <div style={styles.section}>
        <h2 style={styles.sectionTitle}>PR Rates</h2>
        <div style={styles.grid}>
          <div style={styles.card}>
            <Icon path={mdiAccountGroup} size={1.5} style={styles.icon} />
            <h3 style={styles.cardTitle}>NPO & Community</h3>
          </div>
          <div style={styles.card}>
            <Icon path={mdiMusic} size={1.5} style={styles.icon} />
            <h3 style={styles.cardTitle}>Producers/DJs</h3>
          </div>
          <div style={styles.card}>
            <Icon path={mdiOfficeBuilding} size={1.5} style={styles.icon} />
            <h3 style={styles.cardTitle}>Corporate Clients</h3>
          </div>
          <div style={styles.card}>
            <Icon path={mdiPencil} size={1.5} style={styles.icon} />
            <h3 style={styles.cardTitle}>Promotional Article</h3>
          </div>
        </div>
      </div>
    </div>
  );
};

const styles = {
  container: {
    padding: '20px',
    fontFamily: 'Arial, sans-serif',
  },
  title: {
    fontSize: '24px',
    fontWeight: 'bold',
    marginBottom: '10px',
  },
  description: {
    fontSize: '16px',
    marginBottom: '20px',
  },
  section: {
    marginBottom: '20px',
  },
  sectionTitle: {
    fontSize: '20px',
    fontWeight: 'bold',
    marginBottom: '15px',
  },
  grid: {
    display: 'grid',
    gridTemplateColumns: 'repeat(auto-fit, minmax(200px, 1fr))',
    gap: '20px',
  },
  card: {
    textAlign: 'center',
    padding: '20px',
    border: '1px solid #ddd',
    borderRadius: '8px',
  },
  icon: {
    marginBottom: '10px',
  },
  cardTitle: {
    fontSize: '18px',
    fontWeight: 'bold',
  },
};

export default Services;
