import React from 'react';
import person1 from './../../assets/person1.jpg';
import person2 from './../../assets/person2.jpg';

const Testimonials = () => {
  return (
    <>
      <section className="testimonials-section">
        <div className="testimonials-content">
          <div className="testimonials-text">
            <h1 style={{ color: '#5cb6f9', fontSize: '3rem' }}>What Our Clients Say</h1>
            <p className="section-subtitle" style={{ color: 'white', fontSize: '1.5rem' }}>Testimonials and Reviews</p>
          </div>
          <div className="testimonials-cards">
            <div className="testimonials-card">
              <img src={person1} alt="John Doe" className="testimonial-image" />
              <p style={{ color: 'white', fontSize: '1.5rem' }}>
                "Mabunda Tech has been a game-changer for our business. Their mobile app development services are top-notch and their team is very professional."
              </p>
              <h3 style={{ color: '#5cb6f9', fontSize: '1.5rem' }}>Dineo Leshabane.</h3>
            </div>
            <div className="testimonials-card">
              <img src={person2} alt="Jane Smith" className="testimonial-image" />
              <p style={{ color: 'white', fontSize: '1.5rem' }}>
                "We were blown away by the quality of work delivered by Mabunda Tech. Their web development services are exceptional and we highly recommend them."
              </p>
              <h3 style={{ color: '#5cb6f9', fontSize: '1.5rem' }}>Hlayiseka Mabunda</h3>
            </div>
          </div>
        </div>
      </section>

      <style jsx>{`
        .testimonials-section {
          padding: 100px 0;
          background-color: #00183d; 
        }

        .testimonials-content {
          max-width: 1200px;
          margin: 0 auto;
          padding: 0 20px;
        }

        .testimonials-text {
          margin-bottom: 40px;
          text-align: center;
        }

        .testimonials-cards {
          display: flex;
          flex-wrap: wrap;
          justify-content: center;
        }

        .testimonials-card {
          background-color: #01224a;
          padding: 20px;
          border-radius: 10px;
          box-shadow: 0 0 10px rgba(0, 0, 0, 0.1);
          width: calc(30% - 20px);
          margin: 20px;
          text-align: center; /* Center the text and image */
        }

        .testimonial-image {
          width: 80px; /* Adjust width as needed */
          height: 80px; /* Adjust height as needed */
          border-radius: 50%; /* Make the image circular */
          margin-bottom: 15px; /* Space between image and text */
        }

        .testimonials-card p {
          font-size: 1.2rem;
          margin-bottom: 20px;
          color: white;
        }

        .testimonials-card h3 {
          font-size: 1.5rem;
          margin-top: 0;
          color: #02f7ee;
        }

        /* Responsive styles */
        @media (max-width: 768px) {
          .testimonials-card {
            width: calc(100% - 40px);
            margin: 10px;
            padding: 15px; /* Adjust padding for smaller screens */
          }

          .testimonials-text h1 {
            font-size: 2.5rem; /* Adjust font size for heading */
          }

          .testimonials-text p {
            font-size: 1.2rem; /* Adjust font size for subtitle */
          }

          .testimonials-card h3 {
            font-size: 1.3rem; /* Adjust font size for names */
          }
        }

        @media (max-width: 480px) {
          .testimonials-text h1 {
            font-size: 2rem; /* Further adjust for very small screens */
          }

          .testimonials-text p {
            font-size: 1rem; /* Further adjust for very small screens */
          }

          .testimonials-card h3 {
            font-size: 1.1rem; /* Further adjust for very small screens */
          }
        }
      `}</style>
    </>
  );
};

export default Testimonials;
