import React from 'react';

const HeroSection = () => {
  return (
    <>
      <section className="hero-section">
        <div className="hero-content">
          <h1>Welcome to Our Company</h1>
          <p>Your solution for [describe the solution here].</p>
          <button>Get Started</button>
        </div>
        <img src={require('./../../assets/code.jpg')}  />
      </section>

      <style jsx>{`
        .hero-section {
          background: linear-gradient(to right, #007bff, #00d4ff); /* Background gradient */
          padding: 50px 0;
          color: white;
          text-align: center;
        }

        .hero-content h1 {
          font-size: 2.5rem;
          margin-bottom: 20px;
        }

        .hero-content p {
          font-size: 1.25rem;
          margin-bottom: 30px;
        }

        .hero-content button {
          background-color: #00d4ff;
          color: white;
          padding: 10px 20px;
          border: none;
          font-size: 1rem;
          cursor: pointer;
          border-radius: 5px;
        }

        .hero-content button:hover {
          background-color: #007bff;
        }
      `}</style>
    </>
  );
};

export default HeroSection;
